UPDATE `npc_text_locale` SET `ID`='9637', `Locale`='esES', `Text0_0`='El Círculo de los Retos era antaño un lugar de honor y gloria. Pero entonces llegó el Cártel Bonvapor con sus carros llenos de oro y sus plumas más afiladas que hachas de orco...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9637') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9638', `Locale`='esES', `Text0_0`='Los Sethekk se creen los verdaderos y más fieles seguidores del maestro de todos los arakkoa. No me preguntes por el maestro, $r, porque sus misterios son solo para los alados.$B$BLos Sethekk interpretaron la destrucción de Auchindoun como la llegada de nuestro maestro a este planeta. Partieron hacia las ruinas llevándose las reliquias de nuestro gran héroe, Terokk.$B$BSiguen buscando al maestro en las ruinas del templo, pero he conseguido ver la verdad que se esconde tras sus mentiras. Ya no soy uno de ellos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9638') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9640', `Locale`='esES', `Text0_0`='El Rey \'Garra Ikiss\' es uno de los líderes de los Sethekk, junto con mi hermano, Tejeoscuro Syth.$B$BFueron ellos quienes guiaron el éxodo de los Sethekk desde Skettis hasta las Ruinas de Auchindoun. Al principio, Ikiss era un líder carismático, pero la locura se ha apoderado de él.$B$B¡Ahora se proclama a sí mismo como la reencarnación de Terokk! Realmente espera que los arakkoa se arrodillen ante él como su verdadero gobernante. ¿Te puedes creer su arrogancia?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9640') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9641', `Locale`='esES', `Text0_0`='Es el héroe más glorioso entre los arakkoa.$B$BDicen que, en la antigüedad, construyó Skettis con sus propias garras. Era nuestro mayor campeón y defensor, pero nos dejó cuando dejamos de venerar a Rukhmar.$B$BEmpezamos a adorar a un nuevo maestro, uno que nos prometió increíble poder y estatus en el cosmos.$B$BAlgunos dicen que Terokk y Rukhmar son uno y que nos abandonó cuando nosotros le abandonamos a él.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9641') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9642', `Locale`='esES', `Text0_0`='¡Mucho brazo y poco seso! Así son los peones. Trabajan bien cuando eres capaz de motivarlos, pero de vez en cuando necesitan un poco más de \"motivación\", ¿entiendes?', `Text0_1`='¡Mucho brazo y poco seso! Así son los peones. Trabajan bien cuando eres capaz de motivarlos, pero de vez en cuando necesitan un poco más de \"motivación\", ¿entiendes?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9642') AND (`Locale`='esES');
