UPDATE `npc_text_locale` SET `ID`='9788', `Locale`='esES', `Text0_0`='¿Para qué? Si los demonios de Illidan no me capturan y me vuelven a esclavizar, entonces serán los nagas de la Marisma de Zangar quienes lo hagan.$B$BUnirse a Illidan y a sus aliados significa esclavitud para las tribus de Tábidos. A mi pueblo no le queda esperanza.', `Text0_1`='¿Para qué? Si los demonios de Illidan no me capturan y me vuelven a esclavizar, entonces serán los nagas de la Marisma de Zangar quienes lo hagan.$B$BUnirse a Illidan y a sus aliados significa esclavitud para las tribus de Tábidos. A mi pueblo no le queda esperanza.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9788') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9789', `Locale`='esES', `Text0_0`='¡Juro que jamás volveré a ir por un atajo! ¿Cómo se supone que iba a saber que los cielos sobre la Península del Fuego Infernal eran tan peligrosos?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9789') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9800', `Locale`='esES', `Text0_0`='<Una suave luz te llena según te vas acercando al naaru. El lento repicar de campanas resuena en tu mente y, aunque nadie dice ni la más mínima palabra, sientes una gran seguridad. $gBienvenido:Bienvenida; a la Ciudad de Shattrath.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9800') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9801', `Locale`='esES', `Text0_0`='Sigue tu camino; a menos que tengas algo que ver con la flota.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9801') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9826', `Locale`='esES', `Text0_0`='La Tormenta Abisal\' no ha sido siempre como lo que ves ahora.$B$BHubo un tiempo cuando esta tierra destrozada se conocía con el nombre de las Llanuras de Farahlon. Muy poco de lo que quedaba de esos días ha sobrevivido al mal que se ha instalado aquí.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9826') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9827', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡$C, no ataques! Es solo un disfraz. Soy una espía que trabaja para la coalición del Bajo Arrabal fuera de la Ciudad de Shattrath.$B$B¡Espero que estés aquí para ayudar!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9827') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9828', `Locale`='esES', `Text0_0`='El archimago Vargoth me confió su bastón cuando me apresuró a huir del ataque de los elfos de sangre a la Torre Violeta. Hice lo que me pidió, esperando poder refugiarme en el viejo asentamiento draenei de Arklon.$B$BPero no fue así. Había dado de pleno con una fuerza de la Legión Ardiente. Los demonios me robaron el bastón, aunque conseguí escapar con vida.$B$BAunque mi maestro nada sabe de mi fracaso, sigue siendo un motivo de gran vergüenza para mí.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9828') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9832', `Locale`='esES', `Text0_0`='Vaya. Mira qué han traído los vientos de la nada. Entra, siéntate y descansa. no cobramos mucho; seguro que puedes permitírtelo.', `Text0_1`='Vaya. Mira qué han traído los vientos de la nada. Entra, siéntate y descansa. no cobramos mucho; seguro que puedes permitírtelo.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9832') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9833', `Locale`='esES', `Text0_0`='¡¿Es que no descansan nunca?!$B$BSi queremos asegurar un futuro para el pueblo del Bajo Arrabal, debemos ocuparnos del Consejo de la Sombra antes de que provoque un cataclismo aún mayor que El Vertedero de Huesos.$B$BComo el liderazgo de la Ciudad de Shattrath no se moverá rápidamente, nosotros, el pueblo libre del Bajo Arrabal, nos hemos ocupado de este asunto.$B$BPero han descubierto a nuestros espías. Mantendremos a distancia a los agentes del Consejo de la Sombra mientras entras en el laberinto para ver qué descubres.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9833') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9834', `Locale`='esES', `Text0_0`='Hace mucho tiempo, en una dimensión del cosmos inconcebible para la mente humana, nació -si es que se puede usar este verbo- un ser con un poder tal que su entrada en la existencia resquebrajó la realidad a su alrededor.$B$BConocido como Murmur, era la esencia del sonido. ¡Oírlo hablar significaba conocer la muerte eterna!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9834') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9835', `Locale`='esES', `Text0_0`='Existir solo para el caos, su menor susurro significaba la destrucción de mundos enteros.$B$BY, aun así, había quienes intentaban adorar a este ser sin conciencia. Había quienes estaban más locos y se atrevían a pensar en controlarlo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9835') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9836', `Locale`='esES', `Text0_0`='Las mohosas páginas del códice parecen girar por arte de magia, lo que centra tu atención a un capítulo concreto sobre horrores pasado que ningún ojo mortal debería percibir.$B$BEl libro se abre en una página que, de alguna forma, parece ser la \'correcta\', aunque al mismo tiempo la equivocada.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9836') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9837', `Locale`='esES', `Text0_0`='Y cómo los poderosos caían temblando a sus pies, temerosos de que pudiera dirigir sus palabras hacia ellos.$B$BY, aun así, esos charlatanes, esos falsos adoradores de una religión no existente, pretendían negarse a lo inevitable, en un vano intento de controlar a su \'dios\'. Indiferente y, probablemente sin darse cuenta de su presencia, Murmullo bostezó, y ellos cayeron en la inconsciencia.$B$BPero, de alguna forma, uno de ellos consiguió sobrevivir y, en su locura, encontró la forma de traer a Murmullo al mundo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9837') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9838', `Locale`='esES', `Text0_0`='Un mortal con un increíble conocimiento arcano y oscuro que nadie podía superar; desarrolló un método para aumentar su invocación mediante la captura y utilización de almas.$B$BCivilizaciones enteras fueron exterminadas mediante este dispositivo de almas para alimentar el ritual mediante el que él y sus conspiradores traerían a Murmullo a su mundo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9838') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9839', `Locale`='esES', `Text0_0`='Y sí, invocaron a Murmullo.$B$BSe utilizaron poderosas magias para contenerle y silenciarle, gracias al constante goteo de almas de las que se alimentaban. Y, ni aun así, fueron capaces de controlar a la bestia, ni de someterla a su voluntad.$B$BEstos mortales comenzaron a pensar en diferentes estrategias que, una tras una, fracasaron. Y, al hacerlo, debilitaron los rituales, dando a Murmullo, sin querer, un atisbo de libertad.$B$BEra todo lo que necesitaba.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9839') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9840', `Locale`='esES', `Text0_0`='Solo este libro sobrevivió a la catástrofe que destruyó el mundo en el que se encontraba. Aquellos hombres orgullosos e inconscientes pensaron que controlaban a un ser cósmico de poder inimaginable.$B$B¿Acaso tú, dueño del códice, eres como ellos?$B$B¿Has tramado algún plan para invocar a Murmullo o a alguna otra entidad de las mencionadas en esas páginas impías?$B$BYa lo veremos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9840') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9841', `Locale`='esES', `Text0_0`='¡Eh! Me alegro de ver caras nuevas. No tengo muchas visitas y los técnicos no son muy habladores.$B$BApuesto a que te diriges al Área 52, ¿verdad?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9841') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9844', `Locale`='esES', `Text0_0`='Jefe de cohetes Fuselaje, comandante de B.U.M.: Baqueteados Ultraorbitales Mecanizados, a tu servicio.$B$B¿Están todos los sistemas en marcha? Parece que el cohete abisal X-52 todavía necesita mucho trabajo.$B$BVamos, chicos... ¡Estamos a menos CERO segundos!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9844') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9845', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='$GBienvenido:Bienvenida; al Bastión Allerian, $N.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9845') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9846', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Se trata de un trabajo muy delicado, ¿sabes? No puedo estar sometida a presión, o algo acabará rompiéndose.$B$B¡Como la cabeza de Fuselaje!$B$B¿Te estás preguntando sobre mis robots? Siempre estoy trabajando en el nº I. El nº. II y yo conseguimos la capacidad de sentir durante un milisegundo antes de que su condensador cerebral explotase y se volviese loco.$B$BDecidí saltarme el III y IV, y me fui derecha al nº. V. ¡Mi ojito derecho!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9846') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9847', `Locale`='esES', `Text0_0`='Ten cuidado en las ruinas, $c. Los espíritus del Kirin Tor que encontrarás, no te reconocerán como $gamigo:amiga;.$B$BEl custodio y yo somos los únicos cuyos espíritus mantenían un vínculo tan fuerte con este lugar como para conservar nuestra identidad en la muerte.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9847') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9848', `Locale`='esES', `Text0_0`='Kirin\'Var sobrevivió a 20 largos años de incontables asaltos orcos, pero no pudo sobrevivir a las fuerzas de Kael\'thas. Un hombre que se hacía llamar miembro del Kirin Tor nos asesinó a todos, menos a uno.$B$BEl archimago Vargoth sobrevivió al ataque, así que Kael\'thas le encarceló en la Torre Violeta y protegió la Torre con una magia que prohibía el paso a cualquier miembro del Kirin Tor.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9848') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9853', `Locale`='esES', `Text0_0`='¿Lees el cartel de Se busca para Grek?$B$B¡Grek quiere ayudar!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9853') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9854', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Hola. Esta casa de huéspedes es para los débiles y los cansados, para que se recuperen de sus heridas. Sería imposible aceptar más gente. Si estás buscando un lugar donde descansar, deberías preguntar en el distrito de los Aldor o los Videntes.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9854') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9863', `Locale`='esES', `Text0_0`='¿Qué quieres? Siéntate y escoge uno.$B$BSoy Botas, mixólogo jefe de B.U.M. y barman extraordinario.$B$BSi lo que necesitas es un toque mágico, el viejo Botas te lo dará.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9863') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9868', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Eres el corresponsal de la Voz de Gadgetzan? Si me sigues, te enseñaré mi nuevo invento...$B$B<Le dices a la maga que no eres el corresponsal que esperaba.> Está bien. Si necesitas componentes, búscalos. Rapidito. Estoy esperando a alguien.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9868') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9869', `Locale`='esES', `Text0_0`='¡¿Radiaciones?!$B$BNo, eso no tiene que preocuparte. Rayos, hay que ver las cosas que se cree la gente hoy en día.$B$BEntonces, ¿qué te parecen nuestras operaciones ahí abajo? ¡Es una obra de arte! Ver para creer.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9869') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9871', `Locale`='esES', `Text0_0`='Durante años estuve convencido de que hacíamos lo correcto en Lunargenta al intentar alcanzar Terrallende para reunirnos con Kael.$B$BSi hubiera sabido lo que nos deparaba Kael\'thas, no me habría movido de la Plaza Alalcón.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9871') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9872', `Locale`='esES', `Text0_0`='Hay dos notas enormes en el tablón; una es de Papá Ruedas y, la otra, del jefe de cohetes Fuselaje.$B$BLa de Papá parece ser un anuncio en el que solicita la pieza de una máquina. ¡La del jefe de cohetes guarda relación con una recompensa por la cabeza de un enorme gigante de montaña!$B$BLos detalles de ambas están escritos con caligrafía goblin muy pequeña, que, por supuesto, es prácticamente ilegible.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9872') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9875', `Locale`='esES', `Text0_0`='¡¿Trituradoras?! Ya no sirven. ¡Están muy pasadas de moda!$B$BSi vamos a construir cohetes... si vamos a hacer que el X-52 despegue... ¡necesitamos la mejor tecnología!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9875') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9876', `Locale`='esES', `Text0_0`='Soy Khadgar, antiguo aprendiz del guardián caído, Medivh. Seguro que has oído su nombre antes.$B$BYo fui quien descubrió que Medivh había estado poseído por el Maligno, Sargeras, y que había abierto El Portal Oscuro entre este mundo y Azeroth. Si bien honraba a mi maestro, me vi obligado a matarle y desterrar su alma torturada.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9876') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9877', `Locale`='esES', `Text0_0`='Yo luché junto a Lord Anduin Lothar cuando conseguimos expulsar a la Horda de nuestras tierras, en Azeroth. Como heredero del legado de Medivh, no podía hacer otra cosa.$B$BCuando finalmente llegó el momento de invadir este sombrío mundo, y acabar con el mal de los orcos para siempre, di un paso al frente junto a mis amigos Turalyon, Alleria, Danath y Kurdran.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9877') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9878', `Locale`='esES', `Text0_0`='Mis camaradas y yo nos hicimos llamar los Hijos de Lothar, en honor al hombre más bondadoso y desinteresado que jamás conocimos. Hicimos todo lo que pudimos por derrotar a Ner\'zhul y poner fin a la locura.$B$BCuando este mundo condenado se derrumbó a nuestro alrededor, destruimos El Portal Oscuro para evitar que algún otro mal pudiese caer sobre nuestro amado Azeroth.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9878') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9879', `Locale`='esES', `Text0_0`='Atrapados en ese erial, hicimos lo que pudimos por sobrevivir. Con el tiempo, sentimos que una nueva maldad nos rodeaba desde El Vacío Abisal. Peor que los orcos malignos... Era la Legión Ardiente que pretendía tomar... Terrallende.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9879') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9880', `Locale`='esES', `Text0_0`='Mientras que mis camaradas aguantaban en el límite de la creciente oscuridad, utilicé mi magia para llegar a la Gran Oscuridad del Más Allá. Allí, pude sentir seres con una gran Luz y de insondable pureza.$B$BY encontré a A\'dal y a sus naaru. Fue como... un milagro.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9880') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9881', `Locale`='esES', `Text0_0`='<Khadgar sonríe para sí mismo >$B$BConvencí a A\'dal y a sus hermanos para que volviesen aquí conmigo. Creo que solo su poder puede expulsar a la Legión de este universo. Pero, como descubrirás, para ello es necesario que su poder viva en nuestros corazones.$B$BPara luchar contra la oscuridad, debemos convertirnos en faros de su eterna Luz.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9881') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9886', `Locale`='esES', `Text0_0`='¿Qué deseas, $r?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9886') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9894', `Locale`='esES', `Text0_0`='¿Qué es un \"todólogo\"? Mejor sería preguntar qué no es un \"todólogo.\" ¡Significa que lo estudio todo! De hecho, soy tan experto en \"todo\" que te aseguro que soy el etéreo más inteligente que jamás hayas visto, ¡te apuesto lo que quieras!$B$BPerdona. Está hablando el comerciante en mí...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9894') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9895', `Locale`='esES', `Text0_0`='No soy flaco ni oscuro. De hecho, ambas referencias me parecen insultantes.$B$BY ahora, sal de mi vista antes de que te clave el bastón en el culo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9895') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9896', `Locale`='esES', `Text0_0`='Ah. Veo que eres amigo del Consorcio. ¿Te interesaría hacer una compra?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9896') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9899', `Locale`='esES', `Text0_0`='El panel de control cambia y gira sobre sí mismo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9899') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9901', `Locale`='esES', `Text0_0`='<La cabeza de la imagen se vuelve borrosa mientras se funde con lo que la rodea.>$B$BSi hubieras visto las Llanuras de Farahlon, no te creerías que es este mismo lugar. Hace solo unos meses, estos campos no habrían desentonado en los Páramos de Poniente.$B$BYa no queda nada. ¿Cómo ha sido? ¿Y por qué?', `Text0_1`=NULL, `Text1_0`='La tierra está en peor estado de lo que me había imaginado. Si se sigue dañando a este ritmo, Farahlon, eh..., Tormenta Abisal no será más que un erial cósmico.$B$B¿Cuándo parará? Puede que la pregunta correcta sea ¿parará algún día?', `Text1_1`=NULL, `Text2_0`='<La imagen del archimago parece atravesarte con la mirada en lugar de mirarte.>$B$BGalones y corrientes de energía arcana emanan y flotan como si la zona estuviera hundida en un mar de poder mágico. Ni siquiera el primer cataclismo causó tanto daño.', `Text2_1`=NULL, `Text3_0`='¡Me vengaré de Kael\'thas por esta traición al Kirin Tor! Una vez caminó entre nosotros como un hermano, pero ya no somos hermanos.$B$BLlegará la hora de la verdad.$B$B<El archimago asiente, sin que le entusiasme el panorama.>$B$BKael\'thas es mucho más que un simple pícaro mago. Cuando esté liberado de esta torre, Ravandwyr y yo ayudaremos a quien quiera que se oponga a él.', `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9901') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9902', `Locale`='esES', `Text0_0`='Hubo un tiempo cuando el orbe que ves detrás de mí era capaz de ver lo que sucedía en Azeroth.$B$BLa maldición de Kael\'thas cambió todo eso, por supuesto, pero durante nuestros años de aislamiento, muchos de los magos encontraron alivio en sus imágenes.$B$BAlgunos las miraban para recordar, otros para olvidar.', `Text0_1`=NULL, `Text1_0`='<El archimago Vargoth te sonríe irónicamente.>$B$BHay mucho sitio en la Torre Violeta.$B$BAquí me encontrarás en cualquier momento del año.', `Text1_1`=NULL, `Text2_0`='La noche en que Kael\'thas arrasó el pueblo, yo no pude hacer nada para ayudar. Su magia ya me había encerrado en la torre, exacerbando mi tormento al obligarme a ver las muertes de los míos.$B$BY todavía, sus voces me llaman en la distancia, despertándome en mitad de la noche.', `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9902') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9903', `Locale`='esES', `Text0_0`='Si te han enviado a matarme, coge un número y espera tu turno.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9903') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9904', `Locale`='esES', `Text0_0`='¡Habla bajo! No quiero que me vean aquí.$B$B¡Ooh, esto me pone furioso! ¿Pero quién se creen que son esos etéreos?$B$B¡Ahora me toca a mí!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9904') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9905', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Por favor, déjame sola con mi tristeza.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9905') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9914', `Locale`='esES', `Text0_0`='Ese niño me da escalofríos. Se queda ahí de pie todo el día, conversando con su abuelo. Después, se irá a contarle a su madre lo que le ha dicho el abuelo, y ella – por supuesto – solo sonríe y asiente.$B$B¡Corderos! ¡Lemmings! ¡Todos ellos!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9914') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9916', `Locale`='esES', `Text0_0`='Cuando el jefe de cohetes Fuselaje pedía voluntarios para ayudar a construir el cohete abisal X-52, entregué mi currículum para el departamento de seguridad.$B$BPor desgracia, el único puesto que quedaba era el de jefe de ingenieros. Para mi suerte, tengo aquí a Xyrol.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9916') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9917', `Locale`='esES', `Text0_0`='$GBienvenido:Bienvenida;, $gamigo:amiga; del Bajo Arrabal, pero no te demores.$B$BDel interior llegan sonidos de batalla. ¡Mis compatriotas estarán encantados de recibir tu ayuda!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9917') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9918', `Locale`='esES', `Text0_0`='Si trabas amistad con el Bajo Arrabal, puede que hagamos negocios.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9918') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9919', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿No te parece increíble esta tecnología etérea?$B$BYo me conformo con que me dejen acercarme hasta aquí. Voy a aprender mucho... ¡Puede que consiga alguno de estos para mí!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9919') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9920', `Locale`='esES', `Text0_0`='¡Dale caña, $gchico:chica;! Tengo asuntos que atender...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9920') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9921', `Locale`='esES', `Text0_0`='Se dice que aquellos bendecidos por los espíritus de Auchindoun poseen la habilidad de capturar almas poderosas en forma de fragmentos de espíritus. Me he cruzado con algunos de estos fragmentos de espíritus en mis viajes, pero todavía debo desvelar sus secretos.$B$BLo único que he podido percibir es que parecen originarse en las profundidades de la misma Auchindoun. Si por casualidad te cruzas con alguno de estos fragmentos de espíritus, estoy seguro de que te valdría la pena permitirme que te los quite de las manos...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9921') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9922', `Locale`='esES', `Text0_0`='Este constructo de cristal zumba con energía. Ante ti, se proyectan extraños glifos que se desplazan arriba y abajo con rapidez.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9922') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9923', `Locale`='esES', `Text0_0`='¡Ocupado, ocupado! ¡Tanto por hacer, tan poco tiempo!$B$BBueno, ¿dónde he puesto esa pirollave? Sé que está por aquí... ¡me he quemado los dedos con ella hace solo un momento!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9923') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9925', `Locale`='esES', `Text0_0`='Viene bien saber un poco de magia para ese tipo de trabajos. Las ilusiones no pueden confundir a todo el mundo, pero casi siempre funcionan bastante bien.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9925') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9932', `Locale`='esES', `Text0_0`='Hace muchos años, mis hombres y yo seguimos al mago Khadgar y al paladín Turalyon hasta este mundo. Nuestro objetivo era aniquilar a toda la horrible raza orca. Pero algo salió mal en el último momento.$B$BAl final, el mundo oscuro estalló en pedazos y mis amigos se perdieron en el vacío.', `Text0_1`='Hace muchos años, mis hombres y yo seguimos al mago Khadgar y al paladín Turalyon hasta este mundo. Nuestro objetivo era aniquilar a toda la horrible raza orca. Pero algo salió mal en el último momento.$B$BAl final, el mundo oscuro estalló en pedazos y mis amigos se perdieron en el vacío.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9932') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9933', `Locale`='esES', `Text0_0`='Nosotros, los hijos de Lothar, hemos permanecido aquí desde entonces - luchando... muriendo... rogando que llegara un día como el de hoy – un día en el que el Portal se abriera y nuestros hermanos vinieran a llevarnos a casa.$B$BPor más rebosantes de alegría que estemos, el deber sigue llamando. Hay mucho que hacer antes de poder marcharnos de este mundo y descansar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9933') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9935', `Locale`='esES', `Text0_0`='Otro soldado de camino al Bastión del Honor... ¡por la barba de Kurdran, podrán hacer uso de la ayuda!', `Text0_1`='Otro soldado de camino al Bastión del Honor... ¡por la barba de Kurdran, podrán hacer uso de la ayuda!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9935') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9937', `Locale`='esES', `Text0_0`='¡El Consorcio se vengará de los traidores Zaxxis!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9937') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9938', `Locale`='esES', `Text0_0`='Ah, un nuevo campeón para nuestra causa. ¿Qué dices, $N?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9938') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9939', `Locale`='esES', `Text0_0`='$N, los hijos de Lothar han luchado aquí, en la península del Fuego Infernal, durante dos décadas. Nos vemos acosados por todos lados por orcos indígenas y demonios infernales de la Cruzada Ardiente y, aunque nuestros corazones siguen latiendo con fuerza, años de guerra han reducido nuestros números.$B$B¡Tú, y el influjo de la nueva generación de héroes de Azeroth, sois, sin lugar a dudas, una visión bien acogida!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9939') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9942', `Locale`='esES', `Text0_0`='Hace unos veinte años, la Alianza envió la primera Expedición a este mundo con la esperanza de erradicar a los orcos para siempre. Fue un... conflicto terrible. Muchas almas valientes perecieron para asegurar la seguridad de Azeroth.$B$BPero algunas almas permanecen... en combate eterno.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9942') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9943', `Locale`='esES', `Text0_0`='Hace unos veinte años, la Alianza lanzó la primera expedición a este mundo con la intención de eliminar a los orcos para siempre. Fue... una batalla terrible. Murieron muchos valientes para defender a Azeroth. $B$BGracias, $N, por contribuir al descanso de sus almas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9943') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9944', `Locale`='esES', `Text0_0`='Los hombres y mujeres de Bastión del Honor han derramado mucha sangre y han perdido a sus hijos en esta horrible tierra... ¡Y a pesar de eso, siguen luchando con la moral alta!$B$BAhora, tú y tus seguidores de Azeroth podéis ayudar a que cambien las tornas. Gracias a ti ha vuelto la esperanza de regresar a nuestras tierras.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9944') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9947', `Locale`='esES', `Text0_0`='Como representante del Consorcio, siempre tengo en cuenta la influencia que mis acciones ejercen sobre nuestros negocios.$B$BEs una pena que los elfos de sangre no tengan la delicadeza de hacer lo mismo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9947') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9948', `Locale`='esES', `Text0_0`='Mi hermano menor es la nueva promesa del Consorcio. Por supuesto, es demasiado modesto para reconocerlo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9948') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9951', `Locale`='esES', `Text0_0`='Te saludo, $r. Qué hago aquí es una historia para otro momento. Uf, es una de las malas.$B$BMi hermano y yo estamos atrapados aquí, entre nuestro tiempo y el presente. No osamos marcharnos por miedo de causar una paradoja temporal. Esperamos pacientemente hasta que los dragones nos devuelvan a nuestra auténtica línea temporal.$B$B¿Cuánto tiempo hace, preguntas? En realidad no estoy seguro. Pregúntaselo a mi hermano.$B$B¿Te apetece algo de queso?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9951') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9953', `Locale`='esES', `Text0_0`='Sí, has oído bien. Tengo la voz de una joven orca. Pero escucha, $gchico:chica;, los viajes en el tiempo tienen estas cosas.$B$BPor cierto, son 250 años.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9953') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9954', `Locale`='esES', `Text0_0`='Sí, he estado atrapado aquí desde que puedo recordar.$B$BEn mi vida anterior, era armero. ¿Quizás pueda repararte algo?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9954') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9955', `Locale`='esES', `Text0_0`='¡Al fin, alguien ha bajado a hablar conmigo! ¿Quieres oír alguno de mis poemas?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9955') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9956', `Locale`='esES', `Text0_0`='¡Bajando la calle! ¡Mirando el valle! ¡Observando las hojas caer en la calle!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9956') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9958', `Locale`='esES', `Text0_0`='Esta plataforma de teletransporte etérea está cubierta de botones e interruptores de colores vivos, pero parece haber caído en desuso.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9958') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9959', `Locale`='esES', `Text0_0`='¡Todo en venta, $gamigo:amiga;! Grande y pequeño, sea lo que sea lo que necesitas, Hazzin te lo proporcionará.$B$BSi valoras tu vida, no te acerques a la forja de maná Ultris sin reabastecerte. Hazzin es la última oportunidad que tienes.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9959') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9960', `Locale`='esES', `Text0_0`='Confío en que recuperaremos lo que desea el príncipe-nexo Haramad.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9960') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9962', `Locale`='esES', `Text0_0`='Pastos de Bosquehumeante ha abierto establecimientos en Orgrimmar y Forjaz. ¿Qué prepararán este año?', `Text0_1`='Los Pastos de Bosquehumeante han abierto establecimientos en Orgrimmar y Forjaz. ¿Qué prepararán este año?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9962') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9968', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Nunca había visto nada igual a esos dracos abisales. Parece que han heredado el temperamento de su señor, pero, al mismo tiempo, son algo totalmente diferente.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9968') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9969', `Locale`='esES', `Text0_0`='El suelo de este lugar todavía está caliente de sangre recién derramada. ¡Ah, habría dado mi propio ojo para estar aquí cuando los demonios entraron por ese portal y se enfrentaron a los héroes de Azeroth! ¡Menuda batalla!$B$B¡Pero ahora, al otro lado hay una batalla aún más feroz!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9969') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9970', `Locale`='esES', `Text0_0`='Si has venido hasta aquí, debe de ser porque osas aventurarte cerca de la forja de maná Ara.$B$B¿Quieres comprar o vender antes de seguir hasta allí?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9970') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9971', `Locale`='esES', `Text0_0`='Solo hay tres tipos de personas en este mundo: competidores, clientes y empleados. Los competidores se deben aplastar implacablemente, a los clientes se les debe satisfacer, y a los empleados, supervisar estrictamente.$B$BLos contratistas independientes son un mito. Quieren que se les pague mejor que a los empleados, pero que se les trate como a clientes. Nunca confíes en nadie que use ese título para sí.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9971') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9977', `Locale`='esES', `Text0_0`='Cuidado, mortal. tras esta sala hay peligros desconocidos en tu mundo. Camina con cuidado...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9977') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9978', `Locale`='esES', `Text0_0`='Me alegro de verte de nuevo, $N. ¿Quieres que te lleve a la guarida del maestro?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9978') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9980', `Locale`='esES', `Text0_0`='Me alegro de ver a un amigo de Aldor por estos recónditos rincones. Me temo que no soportaré mucha más cháchara de esos elfos insolentes.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9980') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9981', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡$N, ve a la Cuenca de Arathi! ¡Mata! ¡Mata como nunca habías matado antes!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9981') AND (`Locale`='esES');
