UPDATE `npc_text_locale` SET `ID`='9993', `Locale`='esES', `Text0_0`='$GBienvenido:Bienvenida; a la Flecha de la Tormenta, $c.$B$BQue no te disuada que esté presente a través de este dispositivo de imágenes. Tengo muchos enemigos a los que les gustaría verme muerto, así que es solo por precaución.$B$BTe aseguro que estás perfectamente $gseguro:segura; aquí.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9993') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9994', `Locale`='esES', `Text0_0`='¿Hay algo que desees comprar? ¿Hay algo que desees vender?$B$BPor eso estamos aquí, $gamigo:amiga;.$B$BPor favor, dime qué deseas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9994') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9995', `Locale`='esES', `Text0_0`='Así que acabas de llegar de Azeroth, ¿eh? Será mejor que te armes de valor, $c – en esta tierra hay peligros como ninguno al que se haya enfrentado antes la Horda. Soy Nazgrel – la mano derecha de nuestro poderoso Jefe de Guerra, Thrall. Me honró con la tarea de salvaguardar a su valiente expedición y – por todos los espíritus – ¡lo haré!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9995') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9996', `Locale`='esES', `Text0_0`='¡Throm\'ka, $ghermano:hermana;! $gBienvenido:Bienvenida; a Terrallende. La Horda necesita toda la fuerza que pueda reunir en este mundo destrozado. Te prometo que, si es una batalla gloriosa lo que andas buscando, aquí tendrás hasta hartarte.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9996') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9997', `Locale`='esES', `Text0_0`='Hazlo rápido, $c – tenemos una guerra que librar aquí, ¿o no te habías dado cuenta?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9997') AND (`Locale`='esES');
