UPDATE `npc_text_locale` SET `ID`='10001', `Locale`='esES', `Text0_0`='Ah, bienvenido de nuevo, héroe. ¿Has vuelto para volver a proteger al Guardián? ¡No olvides llevarte una cronobengala contigo!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10001') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10003', `Locale`='esES', `Text0_0`='Cuando nuestro Jefe de Guerra oyó que el Portal Oscuro se había vuelto a abrir, reunió de inmediato a sus consejeros para planear esta osada expedición. Aunque varios de sus concejales tienen sus dudas, Thrall mantiene la esperanza de que podríamos descubrir cosas nuevas sobre la historia de nuestro pueblo. Cree que, en algún lugar en medio de las ruinas de este mundo, se encuentra escondida la auténtica historia del pueblo orco.$B$BDebo admitir que en mis momentos más oscuros... temo que la historia debería permanecer sin contar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10003') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10004', `Locale`='esES', `Text0_0`='La Horda ha arriesgado mucho para aventurarse en Terrallende - recursos, mano de obra – incluso la defensa táctica de nuestras tierras está en juego. A pesar de todo, Thrall cree en la promesa de esta tierra devastada. Hay una luz extraña en sus ojos estos días – como si sintiera alguna revelación conmovedora que los demás no sentimos.$B$BCon todo, es mi Jefe de Guerra y mi hermano de sangre. Lo seguiría a través de las mismas puertas del abismo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10004') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10005', `Locale`='esES', `Text0_0`='Throm\'ka, $c. Debes de ser $gnuevo:nueva;. Pareces joven y no tienes aspecto de haber vivido los horrores de estas tierras.$B$B<El sargento Trizacráneo escupe en el suelo>$B$BEso va a cambiar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10005') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10006', `Locale`='esES', `Text0_0`='Nuestra expedición logró cruzar el Portal sin incidentes y estableció una nueva base llamada Thrallmar. Pero, como puedes ver, la Legión Ardiente se instaló y nos aisló de nuestros hermanos. Claramente, los demonios esperan retomar el Portal Oscuro y evitar que reunamos refuerzos de Azeroth.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10006') AND (`Locale`='esES');
