UPDATE `npc_text_locale` SET `ID`='10022', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Esta gente llegó de Shattrath hace algunas noches y no han parado de discutir desde entonces.$B$BMe alegro de ver que no te han asustado. Estaba empezando a temer que ahuyentaran a mis clientes.', `Text1_0`=NULL, `Text1_1`='Espero que esa gente de Shattrath deje de luchar pronto. Están empezando a ponerme los nervios de punta.$B$B¿En qué puedo ayudarte?', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10022') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10024', `Locale`='esES', `Text0_0`='Todavía no eres lo suficientemente $gpoderoso:poderosa; como para luchar en el Ojo de la Tormenta. Vuelve cuando tengas más experiencia.', `Text0_1`='Todavía no eres lo suficientemente $gpoderoso:poderosa; como para luchar en el Ojo de la Tormenta. Vuelve cuando tengas más experiencia.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10024') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10026', `Locale`='esES', `Text0_0`='La Horda no debe permitir que los draenei y su entrometida Alianza controlen las células de maná de Terrallende. ¿Quieres acompañar a los elfos de sangre en su ataque al Ojo de la Tormenta?', `Text0_1`='La Horda no debe permitir que los draenei y su entrometida Alianza controlen las células de maná de Terrallende. ¿Quieres acompañar a los elfos de sangre en su ataque al Ojo de la Tormenta?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10026') AND (`Locale`='esES');
