UPDATE `npc_text_locale` SET `ID`='10031', `Locale`='esES', `Text0_0`='Si has estado en la marisma de Zangar, puede que te hayas encontrado con un tipo llamado Daniels. Ya sabes – lleva un casco extraño, solo responde al nombre de \"el Especialista.\"$B$BPor supuesto, le enseñé todo lo que sabe.$B$BNo te dejes engañar por la ropa elegante. En el fondo sigo siendo un pícaro. Daniels y yo pasamos mucho tiempo juntos en las trincheras, pero eso fue antes de que yo me retirara. Por lo que sé, sigue ahí fuera, desatando el infierno.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10031') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10032', `Locale`='esES', `Text0_0`='Día tras día, lo único que hacemos es matarnos trabajando en esta mina. El elfo de sangre no fue hecho para tales tareas manuales.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10032') AND (`Locale`='esES');
