UPDATE `npc_text_locale` SET `ID`='10045', `Locale`='esES', `Text0_0`='A medida que te acercas a la consola, el cristal de acceso que tienes contigo comienza a zumbar suavemente y a emitir una luz parpadeante.$B$BUn torrente de información fluye hasta tu mente y, de repente, eres consciente de los niveles de producción de energía de la forja de maná, de su estabilidad y de su producción a diferentes tuberías. La consola aguarda tu orden.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10045') AND (`Locale`='esES');
