UPDATE `npc_text_locale` SET `ID`='10057', `Locale`='esES', `Text0_0`='Gracias a ti, podremos seguir con nuestros planes en las Ruinas de Farahlon.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10057') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10058', `Locale`='esES', `Text0_0`='¿Qué sabes de los etéreos, ser de carne?$B$BSin duda nos ves iguales a todos, e ignoras los conflictos y luchas entre las diferentes facciones.$B$BQuizás sea el momento para presentaciones. Soy el comandante Ameer del Protectorado – también tu única esperanza de resolver la infestación de criaturas del vacío y El Etereum en esta zona.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10058') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10059', `Locale`='esES', `Text0_0`='El Etereum era la clase gobernante de etéreos en mi mundo de K\'aresh. Trágicamente, nos vimos obligados a evacuar K\'aresh poco antes de que el señor del vacío, Dimensius, liberara sus ejércitos del vacío sobre el mundo y diezmara por completo todo lo que se hallaba a su paso. Por aquel acto, El Etereum juró venganza. Lleva siguiéndole la pista a Dimensius desde entonces...$B$BEl Protectorado, sin embargo, no fue una reacción a Dimensius. Fuimos una reacción a El Etereum.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10059') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10060', `Locale`='esES', `Text0_0`='El Etereum se ha vuelto oscuro... retorcido. La venganza se ha convertido en su único recurso y, cualquiera que se interponga en su camino o no se una a su causa se considera enemigo. Muchos vieron que El Etereum caía en picado hacia la locura y se marcharon. Varios de los príncipes-nexo del consejo Etereum los abandonaron para formar otros grupos y facciones con diferentes objetivos. El Consorcio es un ejemplo de un grupo disidente. El Protectorado es otro...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10060') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10061', `Locale`='esES', `Text0_0`='Pensamos que El Etereum es una amenaza tan grande para nuestra existencia como Dimensius. Ambos deben ser detenidos...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10061') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10062', `Locale`='esES', `Text0_0`='Hola. Soy Vixton Silbapellizco, vendedor oficial de la Arena para el circuito de lucha de Bonvapor. Si tienes al representante, tengo los artículos que te harán la boca agua.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10062') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10063', `Locale`='esES', `Text0_0`='Si estás $gmareado:mareada;, habla con Pestle. Puede que tenga algo para los mareos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10063') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10064', `Locale`='esES', `Text0_0`='<El aprendiz mira de vez en cuando por encima del hombro, aparentemente reaccionando ante sonidos que tú no puedes oír.>$B$BBuenos días, $N. Discúlpame si parezco distraído, pero me encuentro en medio de un trabajo para el granjero Natin. Quiero poner herraduras a todos sus caballos para cuando vuelva del concejo. ¿En qué puedo ayudarte?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10064') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10065', `Locale`='esES', `Text0_0`='Al agente Ya-six y a mí nos asignaron una misión de prioridad alta desde el mando del Protectorado: erradicar toda señal de infestación de criaturas del vacío de Ultris. Tenían que ser ataques tácticos. Nuestro último ataque antes de volver a la Atalaya del Protectorado tenía que caer sobre un señor del vacío llamado Arconus, que había estado engordando gracias a los seres de carne de la zona.$B$BPor desgracia, llegamos un poco tarde. Ya-six y yo nos separamos en el tiroteo con las bestias de carne. Ahora está atrapado dentro, inmovilizado tras líneas enemigas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10065') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10066', `Locale`='esES', `Text0_0`='Si la Luz no puede guiarte, $gamigo:amiga;, quizás yo pueda ayudarte. ¿Qué necesitas?', `Text0_1`='Si la Luz no puede guiarte, $gamigo:amiga;, quizás yo pueda ayudarte. ¿Qué necesitas?', `Text1_0`='Esta isla puede ser extraña, ¿verdad? ¿Hay algo que pueda hacer por ti?', `Text1_1`='Esta isla puede ser extraña, ¿verdad? ¿Hay algo que pueda hacer por ti?', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10066') AND (`Locale`='esES');
