UPDATE `npc_text_locale` SET `ID`='10257', `Locale`='esES', `Text0_0`='La piel tensada entre los huesos es una llamada a la lucha contra los defensores de la Alianza de Sylvanaar y la Arboleda Viviente al oeste.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10257') AND (`Locale`='esES');
