UPDATE `npc_text_locale` SET `ID`='10264', `Locale`='esES', `Text0_0`='Me alegro de ver a $gun:una; $r como tú ayudando para que Sylvanaar sea un lugar mejor. Hemos hecho mucho en muy poco tiempo, pero las fuerzas que nos rodean amenazan nuestra forma de vida.$B$BMe gustaría escuchar lo que tienes que decir. Habla, $c.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10264') AND (`Locale`='esES');
