UPDATE `npc_text_locale` SET `ID`='10270', `Locale`='esES', `Text0_0`='Como representante de la Liga de Expedicionarios, Bronwyn y yo estamos aquí por petición de la Alianza para estudiar estas montañas.$B$BEntre tú y yo..., cuando volvimos de Silithus necesitábamos unas vacaciones. Este parece un buen sitio para relajarse.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10270') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10271', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Puede que Borgrim tenga razón; es posible que un descanso nos venga bien.$B$BPero ahora que estamos aquí, estoy dispuesta a mancharme las manos y estudiar lo que puede ofrecernos esta tierra.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10271') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10275', `Locale`='esES', `Text0_0`='Los Hijos de Lothar llevan mucho tiempo luchando en una guerra que nunca termina. Es asombroso que nuestro equipo se haya conservado tan bien.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10275') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10280', `Locale`='esES', `Text0_0`='Saludos, $gviajero:viajera; Soy Oronok. Siéntate al lado de mi fuego y caliéntate.$B$B¡Ten cuidado con las hambrientas criaturas que hay por debajo de nosotros!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10280') AND (`Locale`='esES');
