UPDATE `npc_text_locale` SET `ID`='10293', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='La Sociedad Real de Boticarios de Entrañas está dispuesta a ayudar a nuestros aliados orcos aquí, en Terrallende. Es una oportunidad de estrechar nuestros lazos y de encontrar nuevos especímenes y compuestos para nuestros estudios.$B$BCompuestos como... venenos.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10293') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10300', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Eh, no abras las jaulas!$B$BEstos ogros me tienen justo donde quiero.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10300') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10301', `Locale`='esES', `Text0_0`='Tengo entendido que el polvo y el calor de la Península del Fuego Infernal son muy irritantes... No estoy seguro, pero confiaré en mis queridos y todavía vivos aliados.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10301') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10305', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='No podrás comprar ningún halcón zancudo hasta que demuestres tu dedicación por mi pueblo.$B¡No vuelvas hasta que no te consideren exaltado! ¡Vete!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10305') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10306', `Locale`='esES', `Text0_0`='¡Estos orcos deben de tener muchas ganas de hacer suyas estas tierras de Terrallende! Si te dijera cuánto pagan por mi lealtad, me matarías de la envidia.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10306') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10308', `Locale`='esES', `Text0_0`='Si tienes dinero suficiente, puedo enseñarte a montar un halcón zancudo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10308') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10312', `Locale`='esES', `Text0_0`='¿Estás $glisto:lista; para escuchar la historia de la Clave de Condenación, $N?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10312') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10313', `Locale`='esES', `Text0_0`='Muy bien. Las palabras que buscas tienen un nombre, $N. Son la Clave de Condenación. Son las palabras pronunciadas por Gul\'dan para destruir este mundo. El mismo hechizo que cortó el último vínculo que unía a los orcos con los elementos de Draenor.$B$B<Oronok asiente con la cabeza.>$B$BSí, esa clave es lo que debes reconstruir y son sus palabras las que debes pronunciar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10313') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10314', `Locale`='esES', `Text0_0`='La Clave de Condenación permaneció en los aposentos privados de Gul\'dan en Karabor durante veinte años. Ah, claro... Tú no debes de saber lo que es Karabor, ¿verdad?$B$BEl Templo Oscuro...$B$BSí; ahora es propiedad de Illidan Tempestira.$B$BCuando Illidan tomó el control del Templo Oscuro, descubrió la clave. Se dio cuenta de que el daño a este mundo ya estaba hecho, pero pensó que sería mejor guardarla en un lugar seguro para usarlo en el futuro.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10314') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10315', `Locale`='esES', `Text0_0`='Desde El Altar de Condenación ves los espíritus de los condenados, arrodillados, orando. Lo que no ves, soy yo al lado de Gul\'dan.$B$B<Oronok asiente con la cabeza.>$B$BTe dije que en mi vida he sido muchas cosas, ¿verdad? Una de ellas era lugarteniente de Gul\'dan.$B$BHe hecho cosas terribles en mi vida. He matado a hombres, mujeres y niños. He bailado sobre la sangre derramada de draenei... pero...$B$B<Oronok se atraganta.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10315') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10316', `Locale`='esES', `Text0_0`='Nunca olvidaré ese día, $r. Nunca. Aunque hacía tiempo que había abandonado las vías del chamanismo para tomar las de la magia oscura, sentí cómo se cortó el vínculo. Un dolor muy profundo... No podría explicártelo con palabras.$B$BCuando se cortó el vínculo con los elementos, todos los orcos de Draenor cambiaron. Si alguien todavía dudaba del poder de las sombras, esas dudas desaparecieron con los ríos de lava que recorrían la tierra destrozada.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10316') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10317', `Locale`='esES', `Text0_0`='Me sacudió una náusea irresistible. Tras el ritual, volví corriendo a ver a mis hijos. Recogí lo que pude y me fui con ellos. Nos escapamos a las colinas y nos escondimos. No teníamos elección...$B$BNo pasa un día sin que recuerde el corte. Hemos tardado veinte años en volver a conectar con los elementos. Han sido necesarios veinte años para que los elementos empezaran a curarse.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10317') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10318', `Locale`='esES', `Text0_0`='Como dije, Illidan encontró la clave cuando tomó el Templo Oscuro. Dividió el tomo en tres partes y se lo entregó a sus esbirros para que lo protegieran.$B$BMis hijos... Mis hijos han crecido. Cuando supe lo que había hecho Illidan con el código, les pedí que fuesen a recuperarlo.$B$BEncuéntralos, $N. Encuéntralos y recupera la Clave de Condenación.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10318') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10321', `Locale`='esES', `Text0_0`='$GBienvenido:Bienvenida; a Shattrath, sede de los Sha\'tar en Terrallende. ¿Necesitas información?', `Text0_1`='$GBienvenido:Bienvenida; a Shattrath, sede de los Sha\'tar en Terrallende. ¿Necesitas información?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10321') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10322', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Vine aquí para estudiar la primitiva cultura del clan Señor del Trueno.$B$BEran débiles y sucumbieron al poder que los transformó en orcos viles a través de la sangre de Mannoroth.$B$BHe leído todos sus textos. Me interesa especialmente recuperar un artefacto; un tambor, una flecha y una tableta que se perdieron cuando aplastamos a los ogros Aguja del Filo y los expulsamos hasta su bastión.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10322') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10323', `Locale`='esES', `Text0_0`='El cuerpo sin vida de Ar\'tor permanece suspendido en el aire por la magia demoníaca. Siguen absorbiéndolo... incluso muerto.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10323') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10327', `Locale`='esES', `Text0_0`='He... muerto.$B$B¿Por qué sigo aquí? ¿No hay ningún terreno de caza en el que pueda pasar la eternidad?$B$BNo... Antes debo terminar mi tarea. Antes de dejar este mundo, debo recuperar la segunda parte de la Clave de Condenación.', `Text0_1`='He... muerto.$B$B¿Por qué sigo aquí? ¿No hay ningún terreno de caza en el que pueda pasar la eternidad?$B$BNo... Antes debo terminar mi tarea. Antes de dejar este mundo, debo recuperar la segunda parte de la Clave de Condenación.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10327') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10339', `Locale`='esES', `Text0_0`='He pasado mi tiempo en las trincheras y muchos años entrenando a los cachorros. Me alegro de estar en casa. No importa en lo que se haya convertido, este es mi hogar.', `Text0_1`=NULL, `Text1_0`='Ocuparme de esas bestias intrépidas, que se interponen entre mis compañeros de clan y la muerte, es un honor.', `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10339') AND (`Locale`='esES');
