UPDATE `npc_text_locale` SET `ID`='10492', `Locale`='esES', `Text0_0`='Como recompensa por sus años de servicio, Illidan permitió a Kael\'thas que le enviara cinco guerreros para instruirlos bajo su mando. Sería la primera vez que se permitía a elfos de sangre instruirse como cazademonios.$B$BLa instrucción fue brutal. Illidan hizo pasar a los guerreros de Kael por pruebas que vencerían a los cazademonios más expertos.$B$BTres de los elfos de Kael murieron durante la instrucción – otro se volvió loco.$B$BUno lo logró.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10492') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10493', `Locale`='esES', `Text0_0`='Su nombre era Varedis. Para sorpresa de Illidan, no solo tuvo éxito en los atroces ritos de paso que formaban parte de la instrucción; Varedis ya exhibía un mejor dominio de sus poderes que muchos cazademonios veteranos.$B$BIllidan presintió el potencial que había en Varedis y asignó a tres maestros cazademonios para que lo siguieran instruyendo en sus hábitos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10493') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10494', `Locale`='esES', `Text0_0`='Tras un año de instrucción bajo el mando de los tres mejores cazademonios de Illidan, Varedis los había superado.$B$BAl no estar ya satisfecho con el entrenamiento que podían ofrecerle, Varedis se infiltró en el Consejo de la Sombra, donde conoció la existencia de una reliquia poderosa conocida como el Libro de Nombres Viles. Leyó este libro en secreto, memorizando cada pasaje.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10494') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10495', `Locale`='esES', `Text0_0`='Se rumorea que saber el verdadero nombre de un demonio proporciona cierto poder sobre ese demonio.$B$BSe dice que el Libro de Nombres Viles contiene el verdadero nombre de cada demonio que ha existido. Al memorizar los pasajes del libro, Varedis había encontrado una fuente de poder que rivalizaba con el acto de beber del cráneo de Gul\'dan del propio Illidan.$B$BPor petición de Illidan, Varedis dirige ahora el patio de armas de las Ruinas de Karabor – junto con los tres maestros que instruyeron a Varedis.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10495') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10496', `Locale`='esES', `Text0_0`='¡Vete, mortal! Tu calaña no es bienvenida aquí...', `Text0_1`='¡Vete, mortal! Tu calaña no es bienvenida aquí...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10496') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10497', `Locale`='esES', `Text0_0`='No es sorprendente que el potencial de poder como cazademonios de un elfo sea tan alto. Después de todo, los sin\'dorei no poseen la aversión natural a la magia demoníaca que tienen los elfos de la noche.$B$BSin embargo, no espero que muchos de ellos lo logren. La mayoría, incluido Varedis, buscan el poder en sí. Ese poder los consumirá, pues no les impulsa el fuego puro que arde en el interior del corazón de un auténtico cazademonios.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10497') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10500', `Locale`='esES', `Text0_0`='Puedes alejar a un hombre de los hijos de Lothar, pero no puedes sacarle a los hijos de su interior.$B$B<El herrero se encoge de hombros.>$B$BEs difícil de explicar, pero no siento que deba estar en el Bastión del Honor ahora mismo, eso es todo.$B$BQuizás esté destinado a formar parte de algo más grande.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10500') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10501', `Locale`='esES', `Text0_0`='Mumman ha establecido su humilde tienda cerca de la entrada principal de la ciudad, justo después de pasar por la posada. Es un cocinero excelente.', `Text0_1`='Mumman ha establecido su humilde tienda cerca de la entrada principal de la ciudad, justo después de pasar por la posada. Es un cocinero excelente.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10501') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10502', `Locale`='esES', `Text0_0`='La sastrería de Keelen, en la esquina noroeste del Bazar, tiene un telar como el que describes.', `Text0_1`='La sastrería de Keelen, en la esquina noroeste del Bazar, tiene un telar como el que describes.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10502') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10506', `Locale`='esES', `Text0_0`='¡Para cruzar mi sala, debes vencerme en un juego de habilidad! Controla a tu rey cuando estés $gpreparado:preparada; para comenzar...', `Text0_1`='¡Para cruzar mi sala, debes vencerme en un juego de habilidad! Controla a tu rey cuando estés $gpreparado:preparada; para comenzar...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10506') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10508', `Locale`='esES', `Text0_0`='¡Cualquier consejo que pueda dar para ayudarte en tu lucha contra los ogros, lo tendrás!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10508') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10509', `Locale`='esES', `Text0_0`='Los detalles de este anuncio exigen la ejecución de una loba enorme. Parece que tiene su guarida al sur de la Cresta Dentada, cerca de la fortaleza de la Horda.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10509') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10532', `Locale`='esES', `Text0_0`='Las Montañas Filospada son un lugar de grandes oportunidades. Qué mala suerte que algunos consideren estar por encima de los demás en asuntos que se refieren a aprovecharse de tales oportunidades.$B$B¿Quién se cree que es la Expedición de Cenarion para decirnos lo que podemos o no podemos hacer aquí?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10532') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10533', `Locale`='esES', `Text0_0`='¿Negociaciones? ¡NEGOCIACIONES!$B$BPermíteme dejar totalmente clara nuestra postura. Somos un pueblo libre, capaz de hacer lo que queramos y movernos como nos plazca. La Expedición de Cenarion no es dueña de esta cadena montañosa.$B$B¡No habrá negociaciones! Ahora vuelve con los druidas y diles que seguiremos haciendo lo que nos plazca.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10533') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10534', `Locale`='esES', `Text0_0`='Hemos venido a la Foresta Ruuan para ver si podemos restablecer el equilibrio de la naturaleza en estas montañas.$B$BSin embargo, la intromisión de los arakkoa y el Culto Vermis está dificultando la tarea.$B$BAfortunadamente, hay gente como tú y Samia, dispuesta a echarnos una mano.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10534') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10537', `Locale`='esES', `Text0_0`='¡Detecto una carga en tu región paranarbulátrica! Debes de haber utilizado el Cefirium Capacitorium últimamente – no puedes volver a usarlo hasta que se agote la carga...', `Text0_1`='¡Detecto una carga en tu región paranarbulátrica! Debes de haber utilizado el Cefirium Capacitorium últimamente – no puedes volver a usarlo hasta que se agote la carga...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10537') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10538', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Estoy aquí a instancias de mi maestro, el barón Sablecrín, para ayudar a la Expedición de Cenarion con sus problemas con el Culto Vermis.$B$BSoy bastante experta en estas cosas.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10538') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10539', `Locale`='esES', `Text0_0`='Nuaar, ¿te encuentras bien? Hoy no pareces tú.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10539') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10541', `Locale`='esES', `Text0_0`='Cierto, la reunión. Vayamos al grano, entonces.$B$BEl hecho es que la madera y el ganado que se está reuniendo de la Foresta Ruuan, de lo que tú eres responsable, ha disminuido sobremanera.$B$B¡Necesitamos esos recursos, pero has permitido que los druidas de la Expedición de Cenarion se interpongan en nuestro camino!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10541') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10542', `Locale`='esES', `Text0_0`='¡No quiero excusas, exijo resultados!$B$B¿Crees que lo estamos haciendo aquí es una broma? Si no lo hacemos bien, no solo vendrán los llamados bienhechores, sino que también se nos considerará indignos para la elevación en las filas de Alanegra.$B$B¿Es eso lo que quieres?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10542') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10543', `Locale`='esES', `Text0_0`='Al menos podrías intentar sonar algo convincente.$B$BMira, Nuaar, no iba a decirte esto, pero quizás sea lo mejor, porque también va a involucrarte a ti. Maxnar planea un ataque total a los druidas de la Foresta Ruuan. Y pretende aniquilarlos.$B$BYa es lo malo que hayamos estado luchando contra los ogros Peña\'mok, así que no podemos permitirnos tener que encargarnos de otro frente. He organizado una tregua temporal con los arakkoa.$B$BBueno, ¿qué opinas?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10543') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10544', `Locale`='esES', `Text0_0`='Estamos organizando los preparativos definitivos mientras hablamos. El resto dependerá de con qué rapidez puedas organizar tus tropas en la Foresta Ruuan.$B$B¿Crees que puedes ocuparte de eso y tenerlo hecho rápidamente?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10544') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10545', `Locale`='esES', `Text0_0`='¡Esa es la actitud!$B$BMe alegro de que hayamos tenido esta breve reunión. Me siento mucho mejor con respecto al ataque ahora. Como líderes como tú en el frente, ¿cómo vamos a perder?$B$BBueno, ya tienes tus órdenes de marcha. ¡Ahora vuelve a la Foresta Ruuan y haz que suceda!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10545') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10546', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Karazhan no fue siempre tan... siniestro. El oscurecimiento de Karazhan llegó solo con la muerte de Medivh. O con lo que creemos que fue la muerte de Medivh.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10546') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10549', `Locale`='esES', `Text0_0`='Has llegado hasta aquí, bruto. Ya no puedes volver atrás.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10549') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10554', `Locale`='esES', `Text0_0`='¡Esos druidas han explotado una tremenda fuente de energía! Se pueden convertir en animales... proyectar relámpagos y luz lunar intensa... ¡Debo descubrir cómo lo hacen!$B$BQuizás llevan cargas bajo la toga...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10554') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10556', `Locale`='esES', `Text0_0`='<El arakkoa te dedica una mirada fija y encantada, y alterna entre mirarte y mirar a través de ti.>$B$B¿Qué han provocado nuestros esfuerzos, aparte de una abominación mayor que la que intentábamos evitar?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10556') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10559', `Locale`='esES', `Text0_0`='Las células de maná que hay en el interior del Ojo de la Tormenta son muy valiosas. ¡No podemos permitirnos perderlas y que los draenei las encuentren! ¡Eh, tú! ¡Haz algo útil y ayúdanos en la lucha!', `Text0_1`='Las celdas de maná que hay en el interior del Ojo de la Tormenta mana son muy valiosas. ¡No podemos permitirnos perderlas y que los draenei las encuentren! ¡Eh, tú! ¡Haz algo útil y ayúdanos en la lucha!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10559') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10561', `Locale`='esES', `Text0_0`='Este vuelo te lleva al punto remoto de Cresta Canto. ¡Muy peligroso!$B$BPor esto, voy a tener que necesitar una renuncia firmada...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10561') AND (`Locale`='esES');
