UPDATE `npc_text_locale` SET `ID`='10568', `Locale`='esES', `Text0_0`='Hay un viejo dicho, \"Dale un pescado a un hombre; lo has alimentado un día. Enseña a pescar a un hombre y lo habrás alimentado toda una vida.\" Por supuesto, querrás cocinar ese pescado, y ahí es donde entro yo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10568') AND (`Locale`='esES');
