UPDATE `npc_text_locale` SET `ID`='10610', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡La Avanzada Rompeloma es un lugar genial en el que estar destinado! ¡Las aberraciones del Foso Sangrante están tan cerca que se puede oler su infecta sangre! ¿Qué mejor lugar para vengarse de un clan que ha traicionado a sus hermanos y ha hecho un pacto con demonios?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10610') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10612', `Locale`='esES', `Text0_0`='Buentiempo y yo estamos aquí representando los intereses de Sylvanaar.$B$BLos druidas han sido más que acogedores, pero permitir que la Horda pase por aquí me pone los pelos de punta.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10612') AND (`Locale`='esES');
