UPDATE `npc_text_locale` SET `ID`='10614', `Locale`='esES', `Text0_0`='En los tramos orientales de los Campos de Ala Abisal se encuentra la Fortaleza Faucedraco. Es allí donde el cruel clan de orcos Faucedraco tortura a los dracos que capturan intentando doblegarlos a su voluntad. Muchos han perdido la esperanza y ceden a la voluntad de los Faucedraco. Los demás... se niegan a desembarcar por miedo a que las partidas de guerra Faucedraco los capturen y los lleven de vuelta a la fortaleza.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10614') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10615', `Locale`='esES', `Text0_0`='Yo... No tenemos ningún poder. Illidan encontró y entregó a los Faucedraco a su líder - Zuluhed. Este orco era el único responsable de la captura y encarcelamiento de Alexstrasza. ¿Cómo podríamos luchar contra tal poder? ¡Capturó a un Aspecto!$B$BY con igual facilidad, se llevó a mi compañero.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10615') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10616', `Locale`='esES', `Text0_0`='<Neltharaku asiente.>$B$BKarynaku... está prisionera en lo alto de la Fortaleza Faucedraco. He intentado liberarla muchas veces. He fracasado todas ellas... Has luchado antes contra esas bestias, ¿no es cierto?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10616') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10636', `Locale`='esES', `Text0_0`='En este lugar hay misterios que nosotros los orcos estamos desperados por descubrir. Misterios sobre nuestro pasado, misterios sobre nuestro futuro y misterios que ofrecen fugaces vistazos de nuestro interior.$B$BMi esperanza es penetrar en esos misterios... ¿andarás ese camino conmigo?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10636') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10637', `Locale`='esES', `Text0_0`='Hay problemas en el bosque. ¡Debemos descubrir su origen antes de que sea demasiado tarde!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10637') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10640', `Locale`='esES', `Text0_0`='Pronto echaré raíces y dormiré. Antes de descansar hay cosas que se deben hacer.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10640') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10643', `Locale`='esES', `Text0_0`='¡Éxito! Me he ajustado a la Ciudadela del Fuego Infernal y a la magia que hay en su interior. ¡Ah, tanta energía!$B$BHay innumerables orcos viles en aquel lugar... ¡pero eso no es todo! También siento poder demoníaco dentro de esos muros...$B$BSi te abres, podré comprender tu mente -- brevemente --, tomar pluma y pergamino y registrar mis hallazgos para ti...', `Text0_1`='¡Éxito! Me he ajustado a la Ciudadela del Fuego Infernal y a la magia que hay en su interior. ¡Ah, tanta energía!$B$BHay innumerables orcos viles en aquel lugar... ¡pero eso no es todo! También siento poder demoníaco dentro de esos muros...$B$BSi te abres, podré comprender tu mente -- brevemente --, tomar pluma y pergamino y registrar mis hallazgos para ti...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10643') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10645', `Locale`='esES', `Text0_0`='Nuestros enemigos nos rodean. Me gustaría que los Mok\'Nathal tuvieran una vida mejor, pero las tropas contra las que luchamos han escogido otro camino para nosotros.$B$BSi quieres demostrar tu valía a los Mok\'Nathal, ayúdanos a eliminarlos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10645') AND (`Locale`='esES');
