UPDATE `npc_text_locale` SET `ID`='10659', `Locale`='esES', `Text0_0`='Saludos, $c. Si se me permite el atrevimiento, te recuerdo que, mientras me ajusto a la magia de la Ciudadela del Fuego Infernal, sin duda habrá celadores del Fuego Infernal pisándome los talones.$B$BAsí que te pido que seas un miembro honesto de tu Horda y actúes como el héroe... ¡y me protejas!', `Text0_1`='Saludos, $c. Si se me permite el atrevimiento, te recuerdo que, mientras me ajusto a la magia de la Ciudadela del Fuego Infernal, sin duda habrá celadores del Fuego Infernal pisándome los talones.$B$BAsí que te pido que seas un miembro honesto de tu Horda y actúes como el héroe... ¡y me protejas!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10659') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10661', `Locale`='esES', `Text0_0`='La búsqueda de mi pueblo. Nuestra lucha contra la Alianza. La guerra contra los ogros y sus maestros gronn.$B$BTodas estas cosas son un gran peso sobre mis hombros, $c.$B$BHasta a mí me viene bien algo de ayuda de vez en cuando.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10661') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10662', `Locale`='esES', `Text0_0`='Auchindoun es el hogar de los espíritus de mis ancestros. Quizás no lo parezcamos, pero todos los Tábidos fuimos Draenei antaño... nuestros espíritus son los mismos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10662') AND (`Locale`='esES');
