UPDATE `npc_text_locale` SET `ID`='10717', `Locale`='esES', `Text0_0`='No te preocupes. ¡El Puesto Devastación es de lo más sólido! Pero, por si acaso, prepárate para saltar sobre un grifo si la plataforma comienza a romperse...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10717') AND (`Locale`='esES');
