UPDATE `npc_text_locale` SET `ID`='10732', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Ya llegamos?', `Text1_0`=NULL, `Text1_1`='Si los demonios cayeron, ¿de donde han caído?', `Text2_0`=NULL, `Text2_1`='Creo que necesito un baño. <Olfatea> ¡Y tú también necesitas uno!', `Text3_0`=NULL, `Text3_1`='¿Un tauren paladín es una Vaca Sagrada?', `Text4_0`=NULL, `Text4_1`='¿Me veo muy gorda con este traje?', `Text5_0`=NULL, `Text5_1`='Apuesto a que en el fondo, Illidan es un buen tipo. Sólo necesita una mamá, él también.', `Text6_0`=NULL, `Text6_1`='¡Apuesto a que el helado sabría mejor si fuera enchufado!', `Text7_0`=NULL, `Text7_1`='¡Los chicos tienen sapos y las chicas tenemos belleza!' WHERE (`ID`='10732') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10734', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Porqué Terrallende ya no se llama Draenor?', `Text1_0`=NULL, `Text1_1`='¿El Vacío Abisal tiene la forma de una rosquilla? ¿Y exactamente qué significa Abisal? La supervisora de huérfanos dice que no debo preguntar a la gente qué tienen debajo.', `Text2_0`=NULL, `Text2_1`='¿Ya llegamos?', `Text3_0`=NULL, `Text3_1`='Si aguanto la respiración, ¿de qué color me pondría?', `Text4_0`=NULL, `Text4_1`='Si los caballos tienen herraduras, ¿habrá herraduras para draenei?', `Text5_0`=NULL, `Text5_1`='Si la Legión Ardiente son un problema, ¿porqué no sólo les echamos agua? Entonces tan sólo serían Legión.', `Text6_0`=NULL, `Text6_1`='¿Cómo es que comes y bebes tanto y no te pones gord$go:a;?', `Text7_0`=NULL, `Text7_1`='¿Un naaru apaga a la Luz cuando quiere dormir?' WHERE (`ID`='10734') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10735', `Locale`='esES', `Text0_0`='<Morthis observa el cielo.>$B$BEn parte, envidio la libertad que disfrutan Grajo y Corvax. En efecto es una gran bendición estar libre de ataduras terrenales.$B$BEl futuro me verá volar de nuevo, pero antes me debo ocupar de una tarea.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10735') AND (`Locale`='esES');
