UPDATE `npc_text_locale` SET `ID`='10910', `Locale`='esES', `Text0_0`='La forja de cristal vil puede usarse para transformar tus fragmentos de ápices en frascos inestables de la bestia. Estos frascos vuelven al que los bebe más ágil, fuerte y feliz.$B$BElige, abajo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10910') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10911', `Locale`='esES', `Text0_0`='<Al colocar los fragmentos de ápices en la forja de cristal vil y tirar de la palanca, el aparato hace un ruido de otro mundo mientras los convierte en polvo. Unos instantes después tu frasco sale de su \"boca\".>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10911') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10913', `Locale`='esES', `Text0_0`='La forja de cristal Bash\'ir puede usarse para transformar tus fragmentos de ápices en frascos inestables del hechicero. Estos frascos vuelven al que los bebe más ágil y feliz y aumentan la efectividad de sus hechizos de ataque y sanación.$B$BElige debajo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10913') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10914', `Locale`='esES', `Text0_0`='<Cuando colocas tus fragmentos de ápices en el hueco de la forja de cristal Bash\'ir y tiras de la palanca, el aparato, literalmente, los desintegra. Unos momentos después tu frasco aparece a tus pies.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10914') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10916', `Locale`='esES', `Text0_0`='Esperamos que puedas librarnos de nuestros dolores de cabeza. ¡Intenta tener diez hijos, los diez intentando ser algo para lo que no están destinados!$B$BAun así, como padre, hacemos lo que debemos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10916') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='10917', `Locale`='esES', `Text0_0`='¿Tienes hijos, $c?$B$BDeja que te digamos algo, creemos que éramos mucho más felices antes de venir a Ogri\'la y que los cristales nos hicieran más conscientes. Antes los habríamos golpeado en la cabeza para callarlos. Ahora, todo lo que hacen es darnos la lata con el Guardia del cielo Sha\'tari.$B$B¡La ignorancia es felicidad!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='10917') AND (`Locale`='esES');
