UPDATE `npc_text_locale` SET `ID`='11219', `Locale`='esES', `Text0_0`='Vamos, ¡no seas ridículo! ¡Yo soy el único Budd Magallenas!$B$BHe recorrido toda esta tierra, desde lo alto del Monte Hyjal hasta las profundidades del Templo Sumergido. ¡De hecho, yo fui el primero en llamarlo el Templo Sumergido!$B$BSí, fui yo. Pero no lo pienses demasiado.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11219') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='11221', `Locale`='esES', `Text0_0`='Sí, bien, escucha $c. Soy mayor de lo que aparento y muy sabio para mi edad. Harías bien en no hacerme demasiadas preguntas.$B$BMira, no tienes que creerme. Lee lo publicado en cualquier tablón de anuncios. ¡Estoy en todas partes!$B$BPero hazte un favor e ignora esas en las que pone \"SE BUSCA\" en el título. Leves malentendidos, te lo aseguro.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11221') AND (`Locale`='esES');
