UPDATE `npc_text_locale` SET `ID`='11231', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Oh, ¿yo cocinera? Supongo que es cierto. Una camarera tiene que trabajar en la cocina de vez en cuando y yo he aprendido alguna que otra receta a lo largo de los años. Nada especial, pero son lo suficientemente abundantes como para llenar la considerable barriga de Grok.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11231') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='11234', `Locale`='esES', `Text0_0`='Mi hermano Krixx cree que trabajar en Orgrimmar es malo. Nada se parece a la peste de este lugar. ¡Al menos el jefe me paga un plus de peligrosidad!$B$B¿Qué? ¿Quieres saber dónde está ahora el zepelín? ¡Estás de suerte, tengo mi dispositivo de seguimiento de zepelines aquí!', `Text0_1`='Mi hermano Krixx cree que trabajar en Orgrimmar es malo. Nada se parece a la peste de este lugar. ¡Al menos el jefe me paga un plus de peligrosidad!$B$B¿Qué? ¿Quieres saber dónde está ahora el zepelín? ¡Estás de suerte, tengo mi dispositivo de seguimiento de zepelines aquí!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11234') AND (`Locale`='esES');
