UPDATE `npc_text_locale` SET `ID`='11256', `Locale`='esES', `Text0_0`='¿Qué puede hacer un tipo como yo por $gun:una; $r como tú en un momento como este?', `Text0_1`=NULL, `Text1_0`='¿Puedo hacer algo por ti, $c?', `Text1_1`=NULL, `Text2_0`='¡Si estas aquí para tomar parte en los grandes desechos, ponte en la cola!$B$BMi parte viene con retraso.', `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11256') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='11257', `Locale`='esES', `Text0_0`='Me supera, chico. Soy nuevo en el equipo.$B$BPuedo decirte que he matado montones de trols y bebido mucho grog. Lo único que me falta por ver es el tesoro que Budd está venga a prometer...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11257') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='11258', `Locale`='esES', `Text0_0`='¿Te refieres al equipo de Budd? Sí, no respeta a las personas. Cogería a cualquiera que supiera usar una pala.$B$BAcepta no-muertos. Budd los odia, es probable que les tenga miedo.', `Text0_1`=NULL, `Text1_0`='Sí, nunca se sabe quien va a ser el siguiente en unirse, o el siguiente en morir.$B$B¿Mi consejo? No te encariñes con nadie.', `Text1_1`=NULL, `Text2_0`='De acuerdo. Budd ha estado recogiendo gente por todo el mundo.$B$BParece que cuando hay bebida gratis la gente está dispuesta a dejar sus diferencias de lado.', `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11258') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='11259', `Locale`='esES', `Text0_0`='<Se huele a sí mismo y hace una mueca de asco>$B$B¡Ese es el olor de la aventura, $c!', `Text0_1`=NULL, `Text1_0`='<hip>Esscucha esto, $r. Passha un par de díassh luchando con troles salvajes y desssspuésh cuéntame como hueles', `Text1_1`=NULL, `Text2_0`='Oh, eso. Verás, últimamente no ha habido más que grog orco por el campamento.$B$BEl grog orco no parece sentarme bien, tú me entiendes...', `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11259') AND (`Locale`='esES');
