UPDATE `npc_text_locale` SET `ID`='11311', `Locale`='esES', `Text0_0`='Desearía que tuviéramos un sacerdote o un druida aquí. No puedo continuar mis estudios hasta que me haya recuperado de mis heridas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11311') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='11320', `Locale`='esES', `Text0_0`='¡Hola! Esos inútiles piratas nos están bloqueando en la bahía.$B$B¿Crees que puedes ayudarnos lanzándoles unas cuantas bombas?$B$BNuestro zepelín robado está aquí, ¡salta a bordo antes de que zarpe!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11320') AND (`Locale`='esES');
