UPDATE `npc_text_locale` SET `ID`='11330', `Locale`='esES', `Text0_0`='¡Soy un prisionero!$B$BMe derribaron junto a mi precioso zepelín. ¿Y por qué? ¿Por transportar hombres y material para la Horda? ¡Eso no es nuevo!$B$BAhora me obligan a dar vueltas a la bahía con él para bombardear a los piratas. ¡No puedo seguir así! ¡Apenas lo puedo mantener íntegro a base de saliva y alambre!$B$BSi estás seguro de que quieres ayudarles, volverá a atracar en el puerto en menos de un minuto.$B$B¡Te digo que es un crimen!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11330') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='11352', `Locale`='esES', `Text0_0`='Paso el día y la noche aquí sentado haciendo munición y encordando ballestas.$B$B¿Supongo que tu también quieres que haga algo para ti?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11352') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='11356', `Locale`='esES', `Text0_0`='¿Qué está pasando?', `Text0_1`='¿Qué está pasando?', `Text1_0`='¡Quiero regresar al orfanato!', `Text1_1`='¡Quiero regresar al orfanato!', `Text2_0`='¡Supervisora! ¿$gEste hombre malo;Esta mujer mala; va a alimentarnos?', `Text2_1`='¡Supervisora! ¿$gEste hombre malo;Esta mujer mala; va a alimentarnos?', `Text3_0`='¡Si mi papá estuviera vivo, él apagaría los incendios!', `Text3_1`='¡Si mi papá estuviera vivo, él apagaría los incendios!', `Text4_0`='¡Si mi mamá estuviera viva, ella apagaría los incendios!', `Text4_1`='¡Si mi mamá estuviera viva, ella apagaría los incendios!', `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11356') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='11358', `Locale`='esES', `Text0_0`='¿Se ha ido? ¡Hurra!', `Text0_1`='¿Se ha ido? ¡Hurra!', `Text1_0`='El Jinete decapitado era escalofriante, ¡pero no me asustó! ¡No a mí!', `Text1_1`='El Jinete decapitado era escalofriante, ¡pero no me asustó! ¡No a mí!', `Text2_0`='¡El Jinete decapitado se quedó asustado!', `Text2_1`='¡El Jinete decapitado se quedó asustado!', `Text3_0`='Si regresa, ¿volverías a luchar con él?', `Text3_1`='Si regresa, ¿volverías a luchar con él?', `Text4_0`='¡Desearía tener $gun padre:una madre; como tú!', `Text4_1`='¡Desearía tener $gun padre:una madre; como tú!', `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11358') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='11359', `Locale`='esES', `Text0_0`='Halloween es un día de fiesta escalofriante, ¡pero me gusta!', `Text0_1`='Halloween es un día de fiesta escalofriante, ¡pero me gusta!', `Text1_0`='¿Has visto al Jinete decapitado? ¡La supervisora dice que si lo miro, me convertirá en una calabaza!', `Text1_1`='¿Has visto al Jinete decapitado? ¡La supervisora dice que si lo miro, me convertirá en una calabaza!', `Text2_0`='¿Puedes llenar un cubo con agua del barril? ¡Es divertido para chapotear!', `Text2_1`='¿Puedes llenar un cubo con agua del barril? ¡Es divertido para chapotear!', `Text3_0`='La vez pasada ví unos soldados de Ventormenta apagando un incendio. ¡Fueron unos héroes!', `Text3_1`='La vez pasada ví unos soldados de Ventormenta apagando un incendio. ¡Fueron unos héroes!', `Text4_0`='El Jinete decapitado vive en el Monasterio Escarlata. A veces sale y asusta a los grandes, ¡pero a mí no!', `Text4_1`='El Jinete decapitado vive en el Monasterio Escarlata. A veces sale y asusta a los grandes, ¡pero a mí no!', `Text5_0`='Una vez conseguí algunas golosinas de la posada. ¡No puedo esperar para conseguir más!', `Text5_1`='Una vez conseguí algunas golosinas de la posada. ¡No puedo esperar para conseguir más!', `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11359') AND (`Locale`='esES');
