UPDATE `npc_text_locale` SET `ID`='11477', `Locale`='esES', `Text0_0`='Halloween es un día de fiesta escalofriante, ¡pero me gusta!', `Text0_1`='Halloween es un día de fiesta escalofriante, ¡pero me gusta!', `Text1_0`='¿Has visto al Jinete decapitado? ¡Si viene aquí, lucharé con él y le robaré su calabaza!', `Text1_1`='¿Has visto al Jinete decapitado? ¡Si viene aquí, lucharé con él y le robaré su calabaza!', `Text2_0`='Me gusta jugar en el barril de agua pero se que no es para niños.', `Text2_1`='Me gusta jugar en el barril de agua pero se que no es para niños.', `Text3_0`='Me dicen que el barril y los cubos son para combatir incendios. ¿Tu combates incendios?', `Text3_1`='Me dicen que el barril y los cubos son para combatir incendios. ¿Tu combates incendios?', `Text4_0`='El Jinete decapitado vive en el Monasterio Escarlata con el resto de fantasmas y humanos aterradores.', `Text4_1`='El Jinete decapitado vive en el Monasterio Escarlata con el resto de fantasmas y humanos aterradores.', `Text5_0`='¡Están dando golosinas y tratos en la posada! El Guardián Grimwatch dice que si no nos dan un trato... ¡entonces podemos jugarles un truco!', `Text5_1`='¡Están dando golosinas y tratos en la posada! El Guardián Grimwatch dice que si no nos dan un trato... ¡entonces podemos jugarles un truco!', `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11477') AND (`Locale`='esES');
