UPDATE `npc_text_locale` SET `ID`='11494', `Locale`='esES', `Text0_0`='No deberíamos haber dejado a tantos atrás, en Valgarde. ¡El almirante Keller, ese insufrible ególatra!$B$B¿Cómo voy a cumplir mis órdenes con tan pocos? ¡Necesito hombres suficientes para que se queden aquí en la fortaleza y aun así poder asignar las tropas necesarias al Fuerte Vildervar cuando esté listo!$B$BAl menos nos queda algo de esperanza de poder resolver nuestros problemas ahora que estás aquí.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11494') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='11496', `Locale`='esES', `Text0_0`='Aconsejo al Capitán en los asuntos que decide compartir conmigo, pero tengo la sensación de que preferiría escucharte a ti.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='11496') AND (`Locale`='esES');
