UPDATE `npc_text_locale` SET `ID`='12084', `Locale`='esES', `Text0_0`='Cuando aceptes una de estas tareas, te prestaremos un carnero de carreras. Es un préstamo, así que no durará para siempre. El carnero puede parecer lento, pero dale con las riendas y cogerá el ritmo.$B$BA nuestros carneros les encantan las manzanas. Hemos colocado algunos contenedores con manzanas por el camino, así que asegúrate de parar junto a ellos. Comer manzanas hará que tu carnero olvide lo cansado que está.', `Text0_1`='Cuando aceptes una de estas tareas, te prestaremos un carnero de carreras. Es un préstamo, así que no durará para siempre. El carnero puede parecer lento, pero dale con las riendas y cogerá el ritmo.$B$BA nuestros carneros les encantan las manzanas. Hemos colocado algunos contenedores con manzanas por el camino, así que asegúrate de parar junto a ellos. Comer manzanas hará que tu carnero olvide lo cansado que está.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12084') AND (`Locale`='esES');
