UPDATE `npc_text_locale` SET `ID`='12086', `Locale`='esES', `Text0_0`='Una vez que empieces a usar las riendas con el carnero, verás su velocidad cambiar. Comienza andando. Tira un poco de las riendas y se pondrá a trotar. Usa las riendas mientras trota para que vaya a medio galope. Tirar un poco más de las riendas hará que galope.$B', `Text0_1`='Una vez que empieces a usar las riendas con el carnero, verás su velocidad cambiar. Comienza andando. Tira un poco de las riendas y se pondrá a trotar. Usa las riendas mientras trota para que vaya a medio galope. Tirar un poco más de las riendas hará que galope.$B', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12086') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12087', `Locale`='esES', `Text0_0`='Mantener al carnero a medio galope o galope durante demasiado tiempo hará que se canse. Si quieres que tu carnero se recupere un poco, deja que ande o trote. Si hay contenedores de manzanas cerca, llévalo junto a ellos.$B', `Text0_1`='Mantener al carnero a medio galope o galope durante demasiado tiempo hará que se canse. Si quieres que tu carnero se recupere un poco, deja que ande o trote. Si hay contenedores de manzanas cerca, llévalo junto a ellos.$B', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12087') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12088', `Locale`='esES', `Text0_0`='Montar en carnero es más fácil de lo que parece, pero hace falta habilidad para ser un experto. Usa las riendas, come manzanas si puedes y evita que tu carnero se canse.$B', `Text0_1`='Montar en carnero es más fácil de lo que parece, pero hace falta habilidad para ser un experto. Usa las riendas, come manzanas si puedes y evita que tu carnero se canse.$B', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12088') AND (`Locale`='esES');
