UPDATE `npc_text_locale` SET `ID`='12098', `Locale`='esES', `Text0_0`='¡Amigo, lo que tengo aquí es una novedad sin precedentes! ¡Y está en venta a muy buen precio!$B$BVerás, estaba harto de gastar dinero en las tabernas buscando a mi media naranja, así que hice estas gafas y ahora TODO EL MUNDO es la viva imagen de la perfección. ¡No hay posibilidad de error!$B$BSi quieres probarlas, tendrás que acolchar mis bolsillos. ¡Ya que tienes tan buen aspecto te haré un descuento!', `Text0_1`='¡Amigo, lo que tengo aquí es una novedad sin precedentes! ¡Y está en venta a muy buen precio!$B$BVerás, estaba harto de gastar dinero en las tabernas buscando a mi media naranja, así que hice estas gafas y ahora TODO EL MUNDO es la viva imagen de la perfección. ¡No hay posibilidad de error!$B$BSi quieres probarlas, tendrás que acolchar mis bolsillos. ¡Ya que tienes tan buen aspecto te haré un descuento!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12098') AND (`Locale`='esES');
