UPDATE `npc_text_locale` SET `ID`='12103', `Locale`='esES', `Text0_0`='Actualmente, los perros normales ni se me acercan. Me quedé hecho polvo cuando lo descubrí... Me encantaban las bestias.$B$BPero, de todos modos, los canes de peste son buenos sustitutos. No son tan... bonitos. Pero bueno... ¡yo tampoco!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12103') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12104', `Locale`='esES', `Text0_0`='No encontrarás una cerveza mejor en todo Azeroth.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12104') AND (`Locale`='esES');
