UPDATE `npc_text_locale` SET `ID`='12112', `Locale`='esES', `Text0_0`='<Ulkang aúlla y te huele, y tú puedes entender lo que dice.>$B$BEl extraño huargo blanco del norte, Garwal, se ha apoderado de mi manada. Debemos rajarle la garganta para que yo vuelva a ser el huargo alfa.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12112') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12113', `Locale`='esES', `Text0_0`='¡El accidente de Cerrotajo está entorpeciendo la llegada de la cerveza! ¡Y los ataques de los enanos Hierro Negro cada hora no están ayudando!$B$BAsí que si quieres ayudar, este es el trato: te prestaré unas riendas y uno de mis carneros.$B$BPor cada barril que me traigas te daré unas fichas. Hasta dejaré que te quedes con el carnero un ratito más por cada barril$B$B¡PERO una vez que empieces, no podrás repetirlo hasta mañana.$B$B¿Estás seguro de que estás preparado?', `Text0_1`='¡El accidente de Cerrotajo está entorpeciendo la llegada de la cerveza! ¡Y los ataques de los enanos Hierro Negro cada hora no están ayudando!$B$BAsí que si quieres ayudar, este es el trato: te prestaré unas riendas y uno de mis carneros.$B$BPor cada barril que me traigas te daré unas fichas. Hasta dejaré que te quedes con el carnero un ratito más por cada barril$B$B¡PERO una vez que empieces, no podrás repetirlo hasta mañana.$B$B¿Estás seguro de que estás preparado?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12113') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12114', `Locale`='esES', `Text0_0`='Veo que inspeccionas los carneros que hemos obtenido de forma totalmente legal... Sé que quieres descubrir la sensación de montar en un carnero... Pero antes de jugar hay que trabajar.$B$BBueno, cuando se trata de estos carneros jugar y trabajar son lo mismo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12114') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12120', `Locale`='esES', `Text0_0`='¡Ah del barco, asere! ¿Qué ocurre, perro de agua salada?$B$B¡Este lugal es todo extraño, cheche!', `Text0_1`=NULL, `Text1_0`='¡Tienes buen aspecto, compay! ¿Robaste a algún masetero? Será mejor que repartas las riquesas... ¡no seas un sucio perro agarrao, camarada!', `Text1_1`=NULL, `Text2_0`='¡Mira tú allá, compay! ¡Ese tío con escorbuto está cabreando al guajiro! El muy terco va a tragar sal, yunta...  lo van a pasar pol debajo de la quilla y se va a quedar hecho un trapo, asere.', `Text2_1`=NULL, `Text3_0`='¡Es la verdad, compay! ¡No hay marinero más duro que los piratas del Mar del Norte! Mejor no enfadar a nadie, camarada. ¡Fíjate tú! ¡Todos los marineros estúpidos acaban siendo asotaos, compay!', `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12120') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12121', `Locale`='esES', `Text0_0`='¡Y ahora te pones cheche! ¿Intentas ofenderme, pulgoso chucho de tierra firme?$B$BApártate, cobarde pillastre. ¡Voy en pira, t\'aluego!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12121') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12122', `Locale`='esES', `Text0_0`='Así que eres la novedad en la ciudad, ¿eh?$B$B<Zorek escupe al suelo.>$B$BSí...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12122') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12134', `Locale`='esES', `Text0_0`='¡En ninguna otra fiesta se bebe y celebra como en la Fiesta de la cerveza!', `Text0_1`='¡En ninguna otra fiesta se bebe y celebra como en la Fiesta de la cerveza!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12134') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12144', `Locale`='esES', `Text0_0`='¿Sip? ¿Quieres volar? Nop... ¡No-no-no de eso nada!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12144') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12152', `Locale`='esES', `Text0_0`='¡Te doy la bienvenida a la zona de la Fiesta de la cerveza, colega! Hay mucho de lo que disfrutar aquí mientras celebramos el espíritu de la competición amistosa entre destilerías rivales. ¡Avísame si tienes alguna pregunta!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12152') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12153', `Locale`='esES', `Text0_0`='Saludos, $r. Soy Hidalgo, maestro de la caza y uno de los mejores halconeros del mundo. Esta máquina de matar que ves posada sobre mi hombro es Excelsior.$B$BPuede que estés pensando: \"¿Qué diablos hace un halconero?\" Yo te lo diré: ¡cazamos! La halconería es el arte de usar un ave rapaz para cazar y recoger la presa. ¿Quizá te interese aprender a cazar con un ave entrenada? ¡Hidalgo puede enseñarte!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12153') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12154', `Locale`='esES', `Text0_0`='La Fiesta de la cerveza consiste en disfrutar de la mejor comida y bebida de la zona ¡y no exagero! Prueba las cervezas caseras vigorizantes, las salchichas, los pretzels y los quesos. ¡No encontrarás nada tan bueno por tan poco dinero!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12154') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12155', `Locale`='esES', `Text0_0`='Después de la cerveza y las salchichas, la parte más popular de la Fiesta de la cerveza son los souvenirs. Desde las vestimentas tradicionales hasta mini-barriles de sabrosa cerveza, Belbi Tiratrastos tiene toda clase de objetos basados en la Fiesta de la cerveza. ¡Pero asegúrate de que tienes suficientes fichas de la Fiesta de la cerveza, ya que es la única forma de comprarlos! Puedes conseguir fichas ayudando en la zona de la Fiesta.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12155') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12156', `Locale`='esES', `Text0_0`='Las carreras de carneros son una de las grandes tradiciones de la Fiesta de la cerveza. Si quieres más información, habla con Pol Semperámbar junto al establo de carneros. ¡Es muy divertido y puede ser una buena manera de conseguir fichas para la Fiesta de la cerveza!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12156') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12157', `Locale`='esES', `Text0_0`='¡Hola, te doy la bienvenida a la Fiesta de la cerveza! Hay muchas formas de divertirse aquí, así que si tienes alguna pregunta, avísame. Si no, ve a comprar una cerveza y un pretzel.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12157') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12158', `Locale`='esES', `Text0_0`='La comida y la bebida lo son todo en la Fiesta de la cerveza y no hay nada mejor que lo que estamos sirviendo. ¿Crees que miento? Prueba las sabrosas cervezas, salchichas, galletas saladas y los quesos que tenemos. Ya lo verás: ¡lo mejor y lo más barato!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12158') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12159', `Locale`='esES', `Text0_0`='A todo el mundo le gustan esos carneros de carreras de la Fiesta de la cerveza. ¿Quieres saber más? Habla con Driz Pataplaf abajo en el establo de carneros. Pásalo pipa y gana algunas fichas para la Fiesta de la cerveza.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12159') AND (`Locale`='esES');
