UPDATE `npc_text_locale` SET `ID`='12174', `Locale`='esES', `Text0_0`='Tesoros de los elfos de sangre sin igual. No es solo por el oro, la seda o las gemas que usan como materiales; la artesanía sin\'dorei tiene valor por sí misma.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12174') AND (`Locale`='esES');
