UPDATE `npc_text_locale` SET `ID`='12176', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Lo único que elll Loco Jonah Sterling quiele que haga es fregalll la\'h cubielllta\'h todo\' los días.$B$B¿Fregalll las cubielta\'h?$B$B¡Yo soy maga, yo NO friego cubiellltas! Al menos no de la manela mundana.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12176') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12178', `Locale`='esES', `Text0_0`='<Es evidente que este pirata ha bebido demasiado. Parece estar inconsciente por el momento.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12178') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12179', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Entonces, ¿qué va a ser? ¿Ron? ¿Cerveza?$B$B¿O quizá otra cosa?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12179') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12180', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Vas a hacerle una de las tuyas a Jack delante de sus chicos?$B$BEso requiere mucha... estupidez. Bueno, será tu problema. Yo no voy a arriesgar mi vida por menos de un oro. ¡Y será mejor que esperes a que me haya ido antes de hacer nada!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12180') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12181', `Locale`='esES', `Text0_0`='¡Siempre hay una buena razón para beber!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12181') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12182', `Locale`='esES', `Text0_0`='Los enanos Hierro negro están irritados por algo. Vienen casi cada media hora, rompen todos los barriles y se van.$B$BTe estarás preguntando \"¿y a mí qué?\"$B$BTe lo explicare... ¡Los barriles que rompen evitan que regalemos cerveza gratis! Me has oído bien: cerveza gratis.$B$BAsí que, si estás por aquí cuando aparezcan esos enanos, dedica un minutos a ayudar a proteger los barriles.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12182') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12183', `Locale`='esES', `Text0_0`='¡La Fiesta de la cerveza es la festividad perfecta! Pero tiene un inconveniente: los ataques de los enanos Hierro Negro. Tienen lugar casi cada media hora.$B$BLos atacantes se ceban con los barriles que dan cerveza GRATUITA. Y es una cerveza muy buena que regalan los cerveceros.$B$BSi estás por aquí cuando aparezcan los enanos Hierro Negro, echa una mano y protege la cerveza GRATUITA.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12183') AND (`Locale`='esES');
