UPDATE `npc_text_locale` SET `ID`='12188', `Locale`='esES', `Text0_0`='Muchas cosas cambian tanto en tierra como en el agua: tú, ellos... muchas cosas cambian.$B$BLa forma de vida de Komawa se ve amenazada por estos cambios. ¿Qué harás?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12188') AND (`Locale`='esES');
