UPDATE `npc_text_locale` SET `ID`='12216', `Locale`='esES', `Text0_0`='Te doy la bienvenida al majestuoso Lady Mehley. Soy el capitán Torgoley.$B$BSi tienes preguntas acerca del barco formúlalas, si no, disfruta del viaje como yo siempre hago... Después de todo, citando a uno de mis escribas favoritos: Mi experiencia con los barcos es que en ellos uno realiza interesantes descubrimientos acerca del mundo. Descubre que puede vivir sin él.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12216') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12217', `Locale`='esES', `Text0_0`='Sí, ese es su nombre y es un buen barco.$B$BHay mucho que aprender de ese tipo de naves, ya sabes. Un gran poeta dijo una vez: Los barcos pasan en la noche y se comunican con solo pasar, solo una señal y una voz lejana en la oscuridad. Así que, en la vida en el océano, pasamos y nos comunicamos con solo una mirada y una voz, la oscuridad de nuevo y el silencio.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12217') AND (`Locale`='esES');
