UPDATE `npc_text_locale` SET `ID`='12220', `Locale`='esES', `Text0_0`='Ah, sí, eso... el nombre. Bueno... dicen que un buen nombre, como la buena voluntad, se consigue mediante muchas acciones y se pierde con solo una. No vale la pena entrar en detalles de cómo o por qué, digamos, sin más, que así es como me llaman algunos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12220') AND (`Locale`='esES');
