UPDATE `npc_text_locale` SET `ID`='12222', `Locale`='esES', `Text0_0`='Tuve suerte. ¡No muchos tauren llegan a moso de camarote en una fragata como esta! Y Grezzix me deja encargarme de estas increíbles canoas de batalla.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12222') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12227', `Locale`='esES', `Text0_0`='Este portal no es como los demás que llevan de vuelta a Azeroth. Para mantenerlo debemos conseguir más células de maná.$B$BO corremos ese riesgo o perdemos nuestro acceso a la Isla de Quel\'Danas y a La Fuente del Sol.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12227') AND (`Locale`='esES');
