UPDATE `npc_text_locale` SET `ID`='12259', `Locale`='esES', `Text0_0`='Nuestros esfuerzos han dado su fruto. Hemos capturado el Sagrario de Tramo del Sol, $N. Ve a ver si hay alguien a quien puedas ayudar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12259') AND (`Locale`='esES');
