UPDATE `npc_text_locale` SET `ID`='12266', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Casi siento celos de nuestro señor Canto de las Olas. ¡Puede que sea más guapo que yo!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12266') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12267', `Locale`='esES', `Text0_0`='Un nombre poco apropiado para un marinero, ¿no te parece?', `Text0_1`='Un nombre poco apropiado para un marinero, ¿no te parece?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12267') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12268', `Locale`='esES', `Text0_0`='Puede que este canoso perro marino sea el más viejo de la tripulación, pero también es el más duro del grupo. ¡Mira! ¡Mira! ¡Sus brazos parecen los de un ogro!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12268') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12270', `Locale`='esES', `Text0_0`='Hemos jurado hacer todo lo que esté en nuestras manos para proteger a los pasajeros de El Valentía. La línea marítima entre Auberdine y el Puerto de Ventormenta ha de ser segura.', `Text0_1`='Hemos jurado hacer todo lo que esté en nuestras manos para proteger a los pasajeros de El Valentía. La línea marítima entre Auberdine y el Puerto de Ventormenta ha de ser segura.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12270') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12271', `Locale`='esES', `Text0_0`='Hola. Estás a bordo de La Fantasía de la Doncella. Yo soy el capitán Cánula y tienes el privilegio de navegar en el orgullo de los Asaltantes Aguasnegras. Los Velasangre no se nos acercan gracias a mi astuta tripulación y a la gran cantidad de pólvora que llevamos en la bodega. El seguro, ya sabes.$B$BSi tienes alguna pregunta, yo te daré respuestas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12271') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12275', `Locale`='esES', `Text0_0`='Te doy la bienvenida a bordo del Transbordador de Plumaluna. Viajamos entre el Bastión Plumaluna y La Costa Olvidada buscando a quienes se perdieron en el mar. Este mar puede ser un lugar peligroso si no estás preparado.$B$BSi necesitas prepararte, habla con nuestros proveedores. Están más que dispuestos a ayudarte.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12275') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12276', `Locale`='esES', `Text0_0`='En nombre de la tripulación del Espuma de la Luna, te doy la bienvenida $c. Espero que hagas uso de nuestras provisiones antes de partir.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12276') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12277', `Locale`='esES', `Text0_0`='Ten cuidado a bordo del Bendición de Elune, $r. Aunque Elune nos ha bendecido con el descubrimiento de nuestros nuevos amigos, también hemos sufrido la dureza del mar.$B$BSin embargo, recuperar almas perdidas en el mar o en un nuevo mundo es una auténtica bendición de Elune.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12277') AND (`Locale`='esES');
