UPDATE `npc_text_locale` SET `ID`='12288', `Locale`='esES', `Text0_0`='¡Te doy la bienvenida a bordo de El invocador del trueno!$B$BNo prestes atención a esa gente que se queja sobre explosiones y cosas así. Lo digo en serio, todo el mundo comete errores. Hay que tener un poco de confianza.$B$BMira, lo entendería si un capitán hubiera hundido su barco 22 o 23 veces... pero menos que eso no es digno de mención.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12288') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12289', `Locale`='esES', `Text0_0`='<Refunfuña> Creo que esas montañas han aparecido de la nada... ¡las cinco veces!', `Text0_1`=NULL, `Text1_0`='<Refunfuña> ¿Crees que estrellar un zepelín contra el océano es difícil? ¡Más difícil sería hacerlo 4 veces!', `Text1_1`=NULL, `Text2_0`='<Refunfuña> Me gustaría poder decir que nunca nos hemos estrellado contra un lago. Me gustaría poder decir que solo nos hemos estrellado contra un lago una vez... ¡Pero nos hemos estrellado en 4 lagos diferentes, incluido el Lago Lordamere!', `Text2_1`=NULL, `Text3_0`='Me impresiona que las ramas de los pinos no dañen demasiado nuestra nave.$B$BClaro que eso no es excusa para las 3 veces que nos hemos estrellado contra un bosque.', `Text3_1`=NULL, `Text4_0`='Jamás pensé que llegaría a ver Teldrassil... Y te juro que jamás pensé que nos estrellaríamos contra Teldrassil.', `Text4_1`=NULL, `Text5_0`='¡Hemos conseguido atravesar Cima del Trueno dos veces!', `Text5_1`=NULL, `Text6_0`='<Refunfuña> ¡Aún no sé cómo hemos logrado atravesar Las Mil Agujas sin un arañazo, para acabar estrellándonos y ardiendo en El Desierto de Sal!', `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12289') AND (`Locale`='esES');
