UPDATE `npc_text_locale` SET `ID`='12292', `Locale`='esES', `Text0_0`='No puedes culparme por estos contratiempos... ¡Ni siquiera puedo ver por dónde vamos!$B$BAunque quizá eso signifique que sí puedes culparme...$B$B¡No! ¡Espera! Puedo atracar esta nave perfectamente... ¡Deben de ser las órdenes del Capitán! Quizá esté forzándonos al límite y examinando nuestra capacidad de supervivencia y reparación.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12292') AND (`Locale`='esES');
