UPDATE `npc_text_locale` SET `ID`='12363', `Locale`='esES', `Text0_0`='¡No tengo tiempo para charlas, hay trabajo que hacer!', `Text0_1`=NULL, `Text1_0`='¿Tú puedes entenderme? ¡Yo puedo entenderte!', `Text1_1`=NULL, `Text2_0`='$R, ¿por qué iba a querer hablar contigo? ¡Probablemente fue tu gente la que provocó este desastre!', `Text2_1`=NULL, `Text3_0`='Hay almejas que recoger y orcas que matar. ¡Ayuda o apártate!', `Text3_1`=NULL, `Text4_0`='Blurglgr murbrgl... es broma. Te entiendo.', `Text4_1`=NULL, `Text5_0`='¿Ya has averiguado por qué se han vuelto locos nuestros hermanos y hermanas del poblado?', `Text5_1`=NULL, `Text6_0`='Sabía que no deberíamos haber entrado en esas cuevas. ¡No es natural!', `Text6_1`=NULL, `Text7_0`='Algo me huele a chamusquina en el nuevo rey Mrgl-Mrgl, pero no consigo averiguar el qué.', `Text7_1`=NULL WHERE (`ID`='12363') AND (`Locale`='esES');
