UPDATE `npc_text_locale` SET `ID`='12370', `Locale`='esES', `Text0_0`='¡Malditos sean Kael\'thas y sus sangrevil! Han destruido todo por lo que hemos trabajado, pero están locos si creen que no contraatacaremos. Por el momento, Lady Liadrin busca una nueva fuente de poder para nuestra orden. No nos destruirán tan fácilmente.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12370') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12373', `Locale`='esES', `Text0_0`='Al arrasar Orgrimmar no solo atacaron a los orcos, sino a toda la Horda. ¡Fue un ataque contra nuestra existencia!$B$BEl Rey Exánime pretendía sembrar el miedo.$B$BEsto...$B$B<Garrosh señala hacia las paredes fortificadas que le rodean.>$B$BEsto es el resultado de la campaña de Arthas.$B$BLa Horda no teme a nada...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12373') AND (`Locale`='esES');
