UPDATE `npc_text_locale` SET `ID`='12417', `Locale`='esES', `Text0_0`='Hemos perdido a muchos de nuestros jinetes del viento en misiones de exploración al oeste. La Plaga no tiene piedad en sus ataques contra los posibles espías.$B$BPor suerte, he encontrado una solución que solo pone en peligro la vida del jinete. La tasa de supervivencia entre los que han sido transportados hasta las Llanuras de Nasam es del 30 ¡Eso es un 30más que cuando estábamos usando jinetes del viento!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12417') AND (`Locale`='esES');
