UPDATE `npc_text_locale` SET `ID`='12422', `Locale`='esES', `Text0_0`='Tanto Desembarco de Garrosh como Pal\'ea fueron barridos por la niebla. ¡Somos los únicos suficientemente locos como para quedarnos aquí, colega!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12422') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12423', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Cómo quieres que trabaje sin mis cosas y con Palanqueta resoplándome en el cogote?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12423') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12424', `Locale`='esES', `Text0_0`='Lo que ves en esta costa no es fruto de la Plaga. De hecho, creemos que la Plaga también teme a esta niebla... puede que ese sea el motivo por el que no vienen aquí.$B$BVerás, la niebla está viva, colega. Dentro de esos zarcillos de aire yace la muerte...$B$BSi entras en sus dominios, te desvanecerás. ¡La niebla te llevará!$B$BNunca hemos visto a nadie regresar de la niebla con vida.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12424') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12426', `Locale`='esES', `Text0_0`='Desde que llegué aquí he visto horrores que antes solo habitaban en mis peores pesadillas, pero nada podría haberme preparado para lo que se oculta tras la niebla.$B$B<Mootoo asiente.>$B$BAl otro lado de esa niebla, a solo unos metros de aquí, se encuentran unas criaturas sedientas de sangre conocidas como Kvaldir. Nadie ha regresado de la niebla y pocos de los que habitaban aquí cuando se formó la niebla lograron huir. Desde entonces la zona ha sido declarada no habitable por Grito Infernal. Entra bajo tu propia responsabilidad...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12426') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12427', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Yo...tú...$B$B<Mootoo tiembla.>$B$B¿Estás aquí para salvarme?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12427') AND (`Locale`='esES');
