UPDATE `npc_text_locale` SET `ID`='12555', `Locale`='esES', `Text0_0`='Morir por su país es el deber de un soldado. Pero si decir que esta guerra es una locura es traición, entonces soy un traidor.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12555') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12566', `Locale`='esES', `Text0_0`='Concéntrate, mortal. Esta es la línea de frente de una guerra como jamás has visto.$B$BNecesitarás el control total de tus facultades si quieres servirnos de algo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12566') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12572', `Locale`='esES', `Text0_0`='¿Estaremos preparados cuando Ahune salga a la superficie?', `Text0_1`='¿Estaremos preparados cuando Ahune salga a la superficie?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12572') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12575', `Locale`='esES', `Text0_0`='¡Esta región está muy alborotada!$B$BLos oscuros cabalgan de nuevo junto con sus San\'layn, los dragones luchan en su gran guerra de El Nexo y los vrykuls han vuelto a despertar.$B$BTu gente navega hacia el norte en sus barcos de madera y luchan unos contra otros y nosotros, los Kalu\'ak, estamos atrapados en medio.$B$BY ahora, mediante alguna temible maldad, la tierra explota con la energía mágica del mundo. Tenemos que encontrar un refugio seguro donde tengamos una posibilidad de sobrevivir. Estos dos embajadores me dan dolor de cabeza... quizá tú puedas ayudar a mi gente, $r.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12575') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12576', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Es un verdadero placer verte de nuevo, $n.', `Text1_0`=NULL, `Text1_1`='¡$n! Me preguntaba cómo te fue. ¿Qué te aguardaba?', `Text2_0`=NULL, `Text2_1`='Confío en que estaras tomando tus precauciones...$B$BEste lugar es peligroso y no me perdonaría si algo malo te ocurriese.', `Text3_0`=NULL, `Text3_1`='Debemos darnos prisa, $n. Los malévolos planes de Malygos pronto serán irreversibles.', `Text4_0`=NULL, `Text4_1`='En tiempos como estos, los amigos queridos son invaluables.$B$BEstáte seguro, $n. Tan seguro como uno pueda estar en un lugar como este...', `Text5_0`=NULL, `Text5_1`='Si no me hubieras rescatado de esa prisión de seguro ya hubiera sucumbido.$B$BNo lo olvidaré...', `Text6_0`=NULL, `Text6_1`='Cuando nuestro deber aquí termine, encontraré la manera de pagarte por mi rescate.', `Text7_0`=NULL, `Text7_1`='Mi fuerza está volviendo, lentamente...$B$BEspero estar apto pronto para mostrarte la gloria de mi verdadera forma.' WHERE (`ID`='12576') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12578', `Locale`='esES', `Text0_0`='Los Kvaldir no tienen piedad, $N. Pero no puedo abandonar la sagrada Kashala. Si no abandonamos a los espíritus, ellos tampoco nos abandonarán.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12578') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12579', `Locale`='esES', `Text0_0`='¡Están atacando Kashala! Debemos mantenerlos a distancia hasta que podamos escapar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12579') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12580', `Locale`='esES', `Text0_0`='¡Malditos hombres-gigante! ¡Si quieren lucha, lucha tendrán!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12580') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12582', `Locale`='esES', `Text0_0`='Saludos, $c. ¿Has venido a honrar esta llama?$B$BRecuerda: las hogueras de nuestro festival arden en todas las regiones con un asentamiento de la Horda. Saca tiempo para honrarlas a todas si puedes.$B$BLa Alianza también tiene sus propios fuegos. Harías bien en profanar esos indignos esfuerzos cuando los veas.', `Text0_1`='Saludos, $c. ¿Has venido a honrar esta llama?$B$BRecuerda: las hogueras de nuestro festival arden en todas las regiones con un asentamiento de la Horda. Saca tiempo para honrarlas a todas si puedes.$B$BLa Alianza también tiene sus propios fuegos. Harías bien en profanar esos indignos esfuerzos cuando los veas.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12582') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12585', `Locale`='esES', `Text0_0`='Este joven colmillarr parece haber muerto en combate en vez de ahogado.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12585') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12590', `Locale`='esES', `Text0_0`='En una sola palabra: magia.$B$BLos irresponsables mortales con sus cortas vidas no piensan en los efectos a largo plazo de sus acciones.$B$BLas energías Arcanas han sido liberadas de forma demasiado liberal, desbaratando el equilibrio del mundo. Si esto sigue así, Azeroth pronto será aniquilado.$B$BAmbas partes están de acuerdo en esto. Pero antes de que se pudiera planear un concilio, Malygos puso en marcha su plan.$B$BNo hemos tenido más elección que enfrentarnos a él.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12590') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12591', `Locale`='esES', `Text0_0`='Estamos progresando, $r, pero demasiado despacio.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12591') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12592', `Locale`='esES', `Text0_0`='¡Tenemos que lograrlo! El destino de Azeroth depende de ello...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12592') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12593', `Locale`='esES', `Text0_0`='Malygos el Tejehechizos, el Señor de la Magia, maestro del Vuelo Azul. Es el aspecto dragón azul, una de las criaturas más antiguas de Azeroth.$B$BDicen que se está recuperando de una locura que ha durado milenios, pero sus acciones recientes me hacen pensar que aún no está cuerdo.$B$BLa solución que propone para nuestro problema es buscar y matar a todos los mortales de Azeroth que usen la magia.$B$BAl ser el guardián de la vida, el Vuelo Rojo se ha visto obligado a entrar en acción.$B$BAhora un dragón lucha contra otro y el destino de nuestro frágil mundo depende del resultado.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12593') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12596', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Me alegra que estés aquí, $c. La situación militar en la Meseta de La Fuente del Sol es bastante desalentadora.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12596') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12597', `Locale`='esES', `Text0_0`='La capitana Selana me ha ordenado que colabore con tus esfuerzos aquí, en la Meseta de La Fuente del Sol.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12597') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12598', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='La Ofensiva Sol Devastado ha realizado un gran progreso en las zonas exteriores de la Isla de Quel\'Danas. Están rechazando a las tropas de Kil\'jaeden y, en medio del caos, he logrado guiar a una pequeña fuerza de exploradores dentro de la Meseta de La Fuente del Sol. Mis tropas avanzaron a través de la Plaza del Parhelio, avistaron a un grupo de elfos de sangre Filosol y se enzarzaron en una lucha contra el enemigo. Esta lucha no tardó en convertirse en un desastre.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12598') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12599', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Los defensores de Kil\'jaeden aquí, en La Fuente del Sol, son mucho más fuertes que cualquier cosa a la que nos hayamos enfrentado fuera de la isla. La Legión ha distorsionado nuestra tecnología de los guardianes Arcanos con energía vil para crear algo mucho más siniestro. Uno solo de esos protectores Filosol diezmó mis tropas y tuve que ordenar una retirada rápida. Esperaba que expulsaran a mi escuadrón de la isla, pero me sorprendieron dejando de perseguirnos.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12599') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12600', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='No sé, pero me alegra que lo hicieran. Este respiro ha permitido al anacoreta Elbadon salvar a muchos de los hombres y mujeres que están bajo mi mando. He enviado a algunos de mis exploradores más sigilosos y me han informado de algo interesante. ¡Casi todos los protectores Filosol se han quedado sin energía! Parece que las tropas de Kil\'jaeden están conservando su energía y parecen satisfechos conteniéndonos con un solo protector activo. Si tú y tus compañeros queréis entrar por la fuerza, permíteme que te sugiera un plan de ataque.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12600') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12601', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Aprovechaos de sus protectores Filosol desactivados. Si alguno de sus exploradores Filosol os ve, seguro que intentará reactivar un protector Filosol cercano. Matadlo rápido para evitar eso. Tras eliminar a los elfos de sangre, siempre y cuando no activéis los sensores de proximidad de los protectores, podréis pasar sin tener que luchar. Eso os ahorrará tiempo y recursos.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12601') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12604', `Locale`='esES', `Text0_0`='Todas las barreras que impedían que llegaras a La Fuente del Sol han sido destruidas. ¡Entra en La Fuente del Sol y evita que Kil\'jaeden entre en este mundo!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12604') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12609', `Locale`='esES', `Text0_0`='¿Estás disfrutando del festival, $c?$B$BMientras los vigilantes de la Llama se ocupan de las hogueras del presente, yo soy más bien un historiador y me interesan mucho los festivales anteriores. Por supuesto, estoy documentando las celebraciones de este año. De momento, todo está saliendo bien, ¿no crees?$B$B¿Sabes, $N? Hay un poder inherente a todas las hogueras del festival que se prenderán durante estas fiestas. Nosotros cuidamos las nuestras pero estoy seguro de que en las ciudades enemigas arden hogueras sagradas...', `Text0_1`='¿Estás disfrutando del festival, $c?$B$BMientras los vigilantes de la Llama se ocupan de las hogueras del presente, yo soy más bien un historiador y me interesan mucho los festivales anteriores. Por supuesto, estoy documentando las celebraciones de este año. De momento, todo está saliendo bien, ¿no crees?$B$B¿Sabes, $N? Hay un poder inherente a todas las hogueras del festival que se prenderán durante estas fiestas. Nosotros cuidamos las nuestras pero estoy seguro de que en las ciudades enemigas arden hogueras sagradas...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12609') AND (`Locale`='esES');
