UPDATE `npc_text_locale` SET `ID`='12617', `Locale`='esES', `Text0_0`='<Budd se queda muy quieto, haciendo como que no te escucha. Inspeccionándolo de cerca puedes ver un poco de baba caer bajo su máscara.>', `Text0_1`=NULL, `Text1_0`='<Budd se balancea en cuclillas, farfullando algo que parece ser lengua trol.>', `Text1_1`=NULL, `Text2_0`='Pírate, colega.', `Text2_1`=NULL, `Text3_0`='No es momento para hablar, colega. Necesito descansar...', `Text3_1`=NULL, `Text4_0`='¡Poder trol, colega!', `Text4_1`=NULL, `Text5_0`='¿Qué te pasa, colega? ¿Nunca has visto un trol?', `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12617') AND (`Locale`='esES');
