UPDATE `npc_text_locale` SET `ID`='12667', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Si consigo crear una nueva máquina de guerra, sé que conseguiré un buen dinerito por ella en la Puerta de Cólera.$B$BAlgunas de nuestras anteriores acciones no han sido del todo... estables, pero mi máquina voladora debería ser suficiente si consigo desarrollar un sistema de bombardeo efectivo para ella.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12667') AND (`Locale`='esES');
