UPDATE `npc_text_locale` SET `ID`='12785', `Locale`='esES', `Text0_0`='Cuando descubrí que tenía parientes aquí, en el Norte, vine a averiguar más cosas. Lo que encontré fueron tribus en lucha peleando contra nuestra propia naturaleza.$B$BNo me queda esperanza, $r. Este lugar maldito ha logrado corromper incluso al gran Ursoc.$B$BHe perdido toda la esperanza.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12785') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12787', `Locale`='esES', `Text0_0`='Sí... Siento la magia de Vordrassil... y no puedo percibir ningún tipo de corrupción.$B$B¡Por favor! Tienes que permitir que te ayude. El destino me ha traído aquí, ¡quizá aún tenga algún propósito!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12787') AND (`Locale`='esES');
